INSERT INTO `grains` VALUES ('1', 'Aromatic Malt', '20.0', '1.036', '20', '1', 'Briess', 'USA', '• European-style dark Munich Malt that provides clean, intensely malty flavor\n• Typical styles are Oktoberest/Marzen, Bock-style, Belgian-style or any beer needing a more malty flavor and aroma\n• Produced in the U.S.A. from AMBA/BMBRI recommended 2-Row Malting Barley varieties\n• 5-15% Any style needing an enhanced malty flavor and aroma\n• Up to 50% Bock/Doppelbock style beers for an exaggerated maltiness', 'Grain', 'http://www.brewingwithbriess.com/Assets/PDFs/Briess_PISB_AromaticMalt.pdf', '2.500', '77.0', '0.0', '0.0', '11.0', '2012-02-21 20:31:57');
INSERT INTO `grains` VALUES ('2', 'Barley, Flaked', '1.0', '1.032', '0', '1', 'Generic', 'USA', 'Use Barley Flakes as an adjunct in all-grain brews to produce a lighter colored finished beer without lowering the original gravity.\n\nUse in place of corn as an adjunct to eliminate corn flavor in the finished beer.\n\nUse at 10-25% of total grist to produce a light colored, mild flavored, dry beer.\n\nBecause flakes have a large surface area and are pre-cooked, they hydrate and disintegrate quickly. Filtration time will be normal.\n\nThere is no need to mill Barley Flakes. However, they can be put through the mill if that is the easiest means of adding them to the mash.\n\n', 'Grain', 'http://www.brewingwithbriess.com/Assets/PDFs/Briess_PISB_IGBarleyFlakes.pdf', '7.000', '75.0', '0.0', '0.0', '13.0', '2012-02-21 20:36:28');
