INSERT INTO `grains` VALUES ('11', 'Amber 50 Malt', '19.0', '1.033', '30', '1', 'Dingemans', 'Belgium', 'The intensive germination and a mildly kilning will add a very strong malt aroma and a deep reddish color. Amber malt clearly improves flavor stability.  The malt is rather low in diastatic power therefore it can only be used up to 30%. \n\nTrappist, Abbey, Amber beers, Ales, Bock beers', 'Grain', 'http://www.dingemansmout.be/sites/dingemansmout.be/files/downloads/AMBER%20MD.pdf', '4.500', '80.0', '78.0', '2.0', '0.0', '2012-02-05 08:47:52');
INSERT INTO `grains` VALUES ('12', 'Caramel Malt 10L', '10.0', '1.033', '0', '1', 'Briess', 'USA', '• A distinguishing characteristic of Caramel Malts is their ability to improve foam development and stability and enhance viscosity due to non-fermentable structures they contribute to beer.\n• 3-7% For balance in Pilsners\n• 5-10% California Common Beer, Doppelbocks and Dark Lagers\n• 5-15% Vienna, Marzen and Oktoberfest\n• 10-15% Bitter Ales, Pale Ales, Mild Ales, Strong Ales and Belgian Ales\n• 5-15% Certain styles of Brown Ales, Scotch Ales, Stouts and Porters', 'Grain', 'http://www.brewingwithbriess.com/Assets/PDFs/Briess_PISB_2RowCaramelMalt10L.pdf', '7.000', '77.0', '0.0', '0.0', '0.0', '2012-02-21 20:31:57');
