INSERT INTO `grains` VALUES ('55', 'Pilsen Malt', '1.6', '1.035', '90', '1', 'Dingemans', 'Belgium', 'Light in color and low in protein. Our Pilsen is made from the finest European two-row barley. This malt is well modified and can easily be mashed with a single – temperature infusion. Due to the high enzymatic power it can be used up to 100% of the grain bill in all types of beer.', 'Grain', 'http://www.dingemansmout.be/sites/dingemansmout.be/files/downloads/PILSEN%20MD_0.pdf', '4.500', '81.0', '79.0', '2.0', '11.0', '2012-02-21 20:31:57');
