INSERT INTO `grains` VALUES ('72', 'Wheat, Torrified', '1.0', '1.034', '0', '1', 'Generic', 'USA', '', 'Grain', '', '7.000', '80.0', '0.0', '0.0', '0.0', '2012-02-05 09:04:12');
INSERT INTO `grains` VALUES ('73', 'Wheat, Flaked', '1.0', '1.034', '0', '1', 'Generic', 'USA', 'Wheat Flakes can be used in place of Wheat Malt to make Wheat Beer. Flakes will yield a different flavor profile than Wheat Malt.\n\nUse in the production of Belgian Wit Beers.\n\nUse up to 40% as a cereal adjunct in the total grist\n\nUse 0.5-1.0% to a standard brew to increase foam stability.\n\nBecause flakes have a large surface area and are pre-cooked, they hydrate and disintegrate quickly. Filtration time will be normal.\n\nThere is no need to mill Wheat Flakes. However, they can be put through the mill if that is the easiest means of adding them to the mash.\n\n', 'Grain', 'http://www.brewingwithbriess.com/Assets/PDFs/Briess_PISB_IGSoftRedWheatFlakes.pdf', '7.000', '80.0', '0.0', '0.0', '16.0', '2012-02-21 20:31:57');
INSERT INTO `grains` VALUES ('74', 'Wheat Malt', '1.6', '1.035', '70', '1', 'Dingemans', 'Belgium', 'Wheat malt gives the specific taste and haze to your wheat beer. Adding wheat malt in smaller amounts will enhance the head retention.', 'Grain', 'http://www.dingemansmout.be/sites/dingemansmout.be/files/downloads/WHEAT_MD_0.pdf', '5.500', '83.0', '80.0', '2.0', '12.0', '2012-02-21 20:31:57');
INSERT INTO `grains` VALUES ('75', 'Wheat, Malted', '2.0', '1.039', '95', '1', 'Ireks', 'Germany', '(Weizen) Small amounts (3-6 %) aid in head retention to any beer without altering final flavor. Use 5 to 70 % in the mash, 40 to 70 % being the norm for wheat beers, combined with a high enzyme malt such as Klages. German Wheat malt is the perfect ingredient for Weiss, Weizen and Berliner Weiss beers. Blended in proportions of 20 to 70% with pale malts, weizen malt is the perfect companion for German wheat strains for a full flavored, classic wheat beer.', 'Grain', '', '0.000', '0.0', '84.0', '0.0', '0.0', '2011-12-22 15:51:30');
INSERT INTO `grains` VALUES ('76', 'Wheat, Malted', '3.0', '1.037', '70', '1', 'Munton\'s', 'England', 'Small amounts (3-6 %) aid in head retention to any beer without altering final flavor. Use 5 to 70 % in the mash, 40 to 70 % being the norm for wheat beers, combined with a high enzyme malt such as Klages.', 'Grain', '', '0.000', '0.0', '80.0', '0.0', '0.0', '2011-12-22 15:51:30');
INSERT INTO `grains` VALUES ('77', 'Wheat Malt, Red', '2.3', '1.037', '180', '1', 'Briess', 'USA', 'Use as part or all of base malt in wheat beers.\n\nRuns efficiently through the brewhouse with slightly higher protein than White Wheat Malt.\n\nOften used in Hefeweizen and other traditional wheat styles due to a distinctive, characteristic wheat flour flavor.\n\nImproves head and foam retention in any beer style.', 'Grain', 'http://www.brewingwithbriess.com/Assets/PDFs/Briess_PISB_WheatMaltRed.pdf', '4.000', '81.0', '79.0', '2.0', '14.0', '2012-02-21 20:31:57');
INSERT INTO `grains` VALUES ('78', 'Wheat Malt, White', '2.5', '1.040', '160', '1', 'Briess', 'USA', 'Use as part or all of base malt in wheat beers.\n\nImproves head and foam retention in any beer style. ', 'Grain', 'http://www.brewingwithbriess.com/Assets/PDFs/Briess_PISB_WheatMaltWhite.pdf', '4.000', '85.0', '84.0', '1.0', '14.0', '2012-02-21 20:31:57');
INSERT INTO `grains` VALUES ('79', 'Wheat, Raw', '1.0', '1.034', '0', '1', 'Generic', 'USA', 'Foam promoting proteins. Protein rest recommended. used in Wit biers at 45% of grist and in Lambics at 30%. Contributes a permenant starch haze to the beer.', 'Grain', '', '7.000', '80.0', '0.0', '0.0', '0.0', '2012-02-05 09:04:44');
INSERT INTO `grains` VALUES ('80', 'Caracrystal Wheat Malt', '55.0', '1.035', '0', '1', 'Briess', 'USA', '• Caracrystal Wheat Malt can be used in an extremely wide variety of beer styles for improved body, foam and foam retention, flavor and color\n• Recommended for all beer styles using caramel malts such as IPAs, Pale Ales, low alcohol beers, wheat beers, Bock beers, and session beers where a touch of subtle caramel plus smooth dark toast flavor is beneficial\n• Use in any beer style to add sweetness plus smooth flavors or to develop complex flavors\n• 5-10% Subtle sweetness and flavor with pale orange tones\n• 10-15% Mild sweetness and flavor plus orange tones\n• 15-25% More pronounced flavor with dark orange to mahogany tones\n', 'Grain', 'http://www.brewingwithbriess.com/Assets/PDFs/Briess_PISB_CaracrystalWheat.pdf', '3.000', '78.0', '0.0', '0.0', '0.0', '2012-02-04 22:04:48');
INSERT INTO `grains` VALUES ('96', 'CBW Pilsen Light LME', '2.0', '1.036', '0', '0', 'Briess', 'USA', '• CBW Pilsen Light LME is 100% pure malted barley extract made from 100% malt and water\n• This is the lightest pure malted barley extract available commercially and produces a very crisp, clear wort\n• CBW Pilsen Light can be used in the production of all extract beer styles and to adjust the color, flavor and gravity of all grain beers\n', 'Extract', 'http://www.brewingwithbriess.com/Assets/PDFs/Briess_PISB_CBWPilsenLightLME.pdf', '0.000', '0.0', '0.0', '0.0', '0.0', '2012-02-05 07:51:02');
INSERT INTO `grains` VALUES ('81', 'Carabrown Malt', '55.0', '1.036', '0', '1', 'Briess', 'USA', '• Carabrown Malt was developed on the light side of the brown malt style in order to retain some residual sweetness while still delivering an assortment of lightly toasted flavors.\n• The overall character of Carabrown Malt is an exceptionally smooth and clean tasting malt that begins with a slightly sweet malty flavor before delivering its payload of toasted flavors, then finishing clean and somewhat dry.\n• Carabrown Malt can be used in a wide variety of beer styles and is especially good in brown ales, porters and stouts.\n• Use in bitter, pale, mild and Belgian Ales, wheat beers, bock, Oktoberfest, and Scotch Ales for flavor, complexity and color.\n• Well suited for many beer styles for subtle flavor and color.\n• 5-10% Subtle sweet malty, lightly toasted flavors and light brown/orange color contributions\n• 10-15% Smooth, more accentuated toasted, biscuity, nutty, graham cracker flavors and slightly dry finish\n• 15-25% More pronounced yet smooth toasted, biscuity, nutty, graham crack', 'Grain', 'http://www.brewingwithbriess.com/Assets/PDFs/Briess_PISB_Carabrown.pdf', '2.200', '79.0', '0.0', '0.0', '0.0', '2012-02-04 22:04:10');
INSERT INTO `grains` VALUES ('82', 'Munich Malt 10L', '10.0', '1.034', '40', '1', 'Briess', 'USA', '• Munich Malt for a clean, malty flavor\n• Typical styles are Alt, Oktoberfest/Marzen or any beer needing a more malty punch\n• Produced in the U.S.A. from AMBA/BMBRI recommended 2-Row Malting Barley varieties\n• 10-20% Any Beer needing an enhanced malty flavor\n• Up to 50% Bock-style beers', 'Grain', 'http://www.brewingwithbriess.com/Assets/PDFs/Briess_PISB_MunichMalt10L.pdf', '3.300', '77.0', '0.0', '0.0', '13.0', '2012-02-21 20:31:57');
INSERT INTO `grains` VALUES ('83', 'Munich Malt 20L', '20.0', '1.033', '20', '1', 'Briess', 'USA', '• Dark Munich Malt that provides clean, intensely malty flavor\n• Typical styles are Oktoberest/Marzen, Bock-style, Belgian-style or any beer needing a more malty flavor and aroma\n• Produced in the U.S.A. from AMBA/BMBRI recommended 2-Row Malting Barley varieties\n• 5-15% Any style needing an enhanced malty flavor and aroma\n• Up to 50% Bock/Doppelbock style beers for an exaggerated maltiness', 'Grain', 'http://www.brewingwithbriess.com/Assets/PDFs/Briess_PISB_MunichMalt20L.pdf', '2.700', '74.0', '0.0', '0.0', '13.0', '2012-02-21 20:31:57');
INSERT INTO `grains` VALUES ('84', 'Carapils', '1.5', '1.032', '0', '1', 'Briess', 'USA', '• Very unique dextrine-style malt that adds body, foam retention and beer stability without influencing color or aroma\n• Use to upgrade all types of beer, including light colored beers\n• The non-fermentables in Carapils® Malt are very advantageous in balancing body and flavor of dark colored beers\n• May be used with or without other specialty malts.\n• Carapils® Malt has the full glassiness of a caramel malt without color and flavor.\n• Carapils® Malt is devoid of enzymes and can be steeped in hot water or mashed.\n• Carapils and Cara-Pils are registered trademarks of Briess Industries, Inc.\n• Low usage rates of 1-5% will help achieve desired results', 'Grain', 'http://www.brewingwithbriess.com/Assets/PDFs/Briess_PISB_2RowCarapilsMalt.pdf', '6.500', '75.0', '0.0', '0.0', '0.0', '2012-02-21 20:31:57');
INSERT INTO `grains` VALUES ('85', 'Smoked Malt', '5.0', '1.034', '140', '1', 'Briess', 'USA', '• Briess Smoked Malt is produced using cherry wood.\n• The result is a unique, enzyme-active specialty malt that contributes an intense smoke, smooth, sweet, smoky flavor.\n• Use in a variety of beer styles to develop complexity or rich, robust smoky flavor:\no Scottish Ales\no Smoked Beer\no Porters\no Bamberger Beer\no Rauch Bock\n• 5-10% Noticeable smoke character in lighter styles such as Scottish Ales and Oktoberfests\n• 10-20% Pronounced smoke character in lighter styles like Scottish Ales and Oktoberfests\n• 30-60% Noticeable to pronounced smoke character in darker styles like Stouts and Porters\n• Briess Smoked Malt delivers intense smoked flavor. We recommend limiting usage to 60% of the grist.', 'Grain', 'http://www.brewingwithbriess.com/Assets/PDFs/Briess_PISB_SmokedMalt.pdf', '6.000', '80.0', '79.0', '1.0', '0.0', '2012-02-04 19:02:40');
INSERT INTO `grains` VALUES ('86', 'Vienna Malt', '3.5', '1.034', '130', '1', 'Briess', 'USA', '• Use as a base malt or high percentage specialty malt\n• High temp kilned malt that retains a great enzyme package for use as a base malt\n• Typical styles are Vienna, Oktoberfest / Marzen, Alt and all dark lagers\n• Produced in the U.S.A. from AMBA/BMBRI recommended 6-Row malting varieties\n• 5-10% When used as one of several High Temp Kilned Malts to develop malty flavor in Vienna, Marzen, Oktoberfest, Bock, Bavarian Weizen and other beers characterized by intense to complex malty flavor\n• 25% For pronounced malty flavor\n• 70-90% Base malt', 'Grain', 'http://www.brewingwithbriess.com/Assets/PDFs/Briess_PISB_ViennaMalt.pdf', '3.800', '77.0', '76.0', '1.0', '13.0', '2012-02-21 20:31:57');
INSERT INTO `grains` VALUES ('87', 'Ashburne Mild Malt', '5.3', '1.034', '65', '1', 'Briess', 'USA', '• Use as a base malt or high percentage specialty malt\n• Typical style inclusion would be Mild Ale, Brown Ale, Belgian Ale, and Barley Wine\n• Slightly darker with a higher dextrin level than Pale Ale Malt\n• Will lend a higher residual maltiness / mouthfeel\n• Produced in the U.S.A. from AMBA/BMBRI recommended 2-Row malting varieties\n•\n• 10-25% Any style needing an enhanced malty flavor and aroma\n• Up to 50% Bock/Dopopelbock style beers for an exaggerated maltiness', 'Grain', 'http://www.brewingwithbriess.com/Assets/PDFs/Briess_PISB_AshburneMildMalt.pdf', '3.500', '79.0', '77.0', '2.0', '11.0', '2012-02-21 20:31:57');
INSERT INTO `grains` VALUES ('88', 'Caramel Malt 90L', '90.0', '1.033', '0', '1', 'Briess', 'USA', '• A distinguishing characteristic of Caramel Malts is their ability to improve foam development and stability and enhance viscosity due to non-fermentable structures they contribute to beer.\n• 1-3% For balance in Pilsners\n• 5-10% California Common Beer, Vienna, Marzen and Oktoberfest\n• 5-15% Doppelbocks, Dark Lagers and Barley Wine\n• 5-15% Certain styles of Brown Ales, Scotch Ales, Stouts and Porters', 'Grain', 'http://www.brewingwithbriess.com/Assets/PDFs/Briess_PISB_CaramelMalt90L.pdf', '4.000', '75.0', '0.0', '0.0', '0.0', '2012-02-21 20:31:57');
INSERT INTO `grains` VALUES ('89', 'Caramel Vienne 20L', '20.0', '1.034', '0', '1', 'Briess', 'USA', '• 3-10% Excellent in Vienna-style lagers and Belgian-style Abbey Ales.', 'Grain', 'http://www.brewingwithbriess.com/Assets/PDFs/Briess_PISB_CaramelVienne20LMalt.pdf', '4.500', '78.0', '0.0', '0.0', '0.0', '2012-02-21 20:31:57');
INSERT INTO `grains` VALUES ('90', 'Caramel Munich 60L', '60.0', '1.034', '0', '1', 'Briess', 'USA', '• Excellent in IPA’s, Pale Ales, Oktoberfests and Porters.', 'Grain', 'http://www.brewingwithbriess.com/Assets/PDFs/Briess_PISB_CaramelMunich60LMalt.pdf', '3.500', '77.0', '0.0', '0.0', '0.0', '2012-02-21 20:31:57');
INSERT INTO `grains` VALUES ('91', 'Victory Malt', '28.0', '1.034', '0', '1', 'Briess', 'USA', '• With an aroma of baking bread, Victory® Malt is well suited for Nut Brown Ales & other dark beers.\n• Its clean flavor makes it well suited for ales and lagers alike\n• Use in small amounts to add complexity and warm color to lighter colored ales and lagers\n• 2-8% Doppelbock, Mild Ales\n• 5-15 Amber ales and lagers, Brown Ales\n• 5-25% Scotch ales and other dark beers', 'Grain', 'http://www.brewingwithbriess.com/Assets/PDFs/Briess_PISB_VictoryMalt.pdf', '2.500', '75.0', '0.0', '0.0', '0.0', '2012-02-21 20:31:57');
INSERT INTO `grains` VALUES ('92', 'Special Roast Malt', '50.0', '1.033', '0', '1', 'Briess', 'USA', '• 5-10% Excellent in IPA, ESB, Amber, Nut Brown Ale, Porter and other dark beer styles', 'Grain', 'http://www.brewingwithbriess.com/Assets/PDFs/Briess_PISB_SpecialRoastMalt.pdf', '2.500', '72.0', '0.0', '0.0', '0.0', '2012-02-04 21:56:02');
INSERT INTO `grains` VALUES ('93', 'Extra Special Malt', '130.0', '1.033', '0', '1', 'Briess', 'USA', '• A unique drum roasting process is employed that produces a wide array of flavors including a combination of both caramel and dry roasting.\n• This unique malt helps develop unique and complex flavors often associated with darker, high gravity beers like Doppelbock.\n• Good for mid to dark Belgian style ales.\n• Adds complex flavors in Abbey styles and in darker styles like dry Irish Stouts and Porters\n• Also provides richer, more complex flavors in lighter colored and lighter gravity beers.\n• Up to 5% Background notes of toasted marshmallow, toast, mild coffee, prunes, dry, woody\n• 5 - 10% Red to amber color with pleasant toffee aroma notes. Belgian ales, English ales for complexity, porters.\n• 10 - 15% Develops a much deeper reddish/brown color with fuller malty body and more pronounce raisin/toasty aroma. Dopplebocks.', 'Grain', 'http://www.brewingwithbriess.com/Assets/PDFs/Briess_PISB_ExtraSpecialMalt.pdf', '2.500', '73.0', '0.0', '0.0', '0.0', '2012-02-04 21:57:07');
INSERT INTO `grains` VALUES ('95', 'Black Malt', '500.0', '1.021', '0', '1', 'Briess', 'USA', '• Use in dark beers for flavor.\n• In small quantities in all styles for color with little or no flavor.\n• Sometimes used with other dark roasted malts to achieve desired color.\n• Often used to provide color without overpowering flavor that is characteristic of European-style dark beers.\n• May be used with or without Roasted Barley to brew a Stout with more color and less intense Roasted Barley flavor notes.\n• May be used with Chocolate Malt to produce a Porter with less pronounced chocolate flavor notes.\n• Black Malt is not interchangeable with Roasted Barley.\n• Produces dark colored beer with little impact to foam color. Use when dark beer with light head is desired.\n• 1-3% All styles for color with little or no flavor\n• 1-10% Porter and Stout', 'Grain', 'http://www.brewingwithbriess.com/Assets/PDFs/Briess_PISB_2RowBlackMalt.pdf', '6.000', '0.0', '0.0', '0.0', '0.0', '2012-02-21 20:31:57');
INSERT INTO `grains` VALUES ('106', 'CBW Traditional Dark LME', '2.0', '1.036', '0', '0', 'Briess', 'USA', '• CBW Traditional Dark LME is 100% pure malted barley extract made from a blend of 100% malt\nand water\n• Use in the production of Red and Amber Beers, Oktoberfest, Bock beer, Doppelbock, brown ales and other dark\ncolored beers', 'Extract', 'http://www.brewingwithbriess.com/Assets/PDFs/Briess_PISB_CBWTradDarkLME.pdf', '0.000', '0.0', '0.0', '0.0', '0.0', '2012-02-05 07:57:13');
INSERT INTO `grains` VALUES ('97', 'CBW Pilsen Light DME', '2.0', '1.045', '0', '0', 'Briess', 'USA', '• CBW Pilsen Light DME is 100% pure malted barley extract made from 100% malt and water\n• All styles of extract beer, and to adjust the color, flavor and gravity of all grain beers', 'Extract', 'http://www.brewingwithbriess.com/Assets/PDFs/Briess_PISB_CBWPilsenLightDME.pdf', '0.000', '0.0', '0.0', '0.0', '0.0', '2012-02-05 07:50:44');
INSERT INTO `grains` VALUES ('100', 'CBW Golden Light LME', '4.0', '1.036', '0', '0', 'Briess', 'USA', '• CBW Golden Light LME is 100% pure malted barley extract made from 100% malt and water\n• CBW Golden Light can be used in the production of all extract beer styles and to adjust the color, flavor and gravity of all grain beers', 'Extract', 'http://www.brewingwithbriess.com/Assets/PDFs/Briess_PISB_CBWGoldenLightLME.pdf', '0.000', '0.0', '0.0', '0.0', '0.0', '2012-02-05 07:50:01');
INSERT INTO `grains` VALUES ('98', 'CBW Bavarian Wheat LME', '3.0', '1.036', '0', '0', 'Briess', 'USA', '• CBW Bavarian Wheat LME is 100% pure malt extract\n• 65% Malted Wheat / 35% Malted Barley\n• All wheat beers, and to adjust the flavor of other styles of beer\n• Use in the production of extract wheat beers, and to adjust the flavor of wheat and other styles of all grain brews', 'Extract', 'http://www.brewingwithbriess.com/Assets/PDFs/Briess_PISB_CBWBavarianWheatLME.pdf', '0.000', '0.0', '0.0', '0.0', '0.0', '2012-02-05 07:49:38');
INSERT INTO `grains` VALUES ('99', 'CBW Bavarian Wheat DME', '3.0', '1.045', '0', '0', 'Briess', 'USA', '• CBW Bavarian Wheat DME is 100% pure malt extract\n• 65% Malted Wheat / 35% Malted Barley\n• CBW Bavarian Wheat can be used in the production of extract wheat beers, and in the production of all grain beers to adjust color, flavor and gravity of wheat and other styles of beer', 'Extract', 'http://www.brewingwithbriess.com/Assets/PDFs/Briess_PISB_CBWBavarianWheatDME.pdf', '0.000', '0.0', '0.0', '0.0', '0.0', '2012-02-05 07:51:50');
INSERT INTO `grains` VALUES ('101', 'CBW Golden Light DME', '4.0', '1.045', '0', '0', 'Briess', 'USA', '• CBW Golden Light DME is 100% pure malted barley extract made from 100% malt and water\n• CBW Golden Light can be used in the production of all extract beer styles and to adjust the color, flavor and gravity of all grain beers', 'Extract', 'http://www.brewingwithbriess.com/Assets/PDFs/Briess_PISB_CBWGoldenLightDME.pdf', '0.000', '0.0', '0.0', '0.0', '0.0', '2012-02-05 07:49:49');
INSERT INTO `grains` VALUES ('102', 'CBW Munich LME', '8.0', '1.036', '0', '0', 'Briess', 'USA', '• CBW Munich LME is 100% pure malted barley extract made from 100% malt and water\n• This specialty malt extract is made using a traditional multiple step mashing process to achieve higher levels of fermentability. It is made from 50% Munich Malt and 50% Base Malt to produce a very rich, malty, amber colored extract\n• Use alone for styles such as Oktoberfest, Marzen or Bock beers\n• Use with a blend of extracts or grains to add rich malty character and reddish hues to any beer style\n\nAvailable only as LME.', 'Extract', 'http://www.brewingwithbriess.com/Assets/PDFs/Briess_PISB_CBWMunichLME.pdf', '0.000', '0.0', '0.0', '0.0', '0.0', '2012-02-05 07:50:31');
INSERT INTO `grains` VALUES ('103', 'CBW Sparkling Amber LME', '10.0', '1.036', '0', '0', 'Briess', 'USA', '• CBW Sparkling Amber LME is 100% pure malted barley extract made from a blend of 100% malt and water\n• Use in the production of Pale Ales, Red and Amber Ales, Munich Style Beers, Bock and Oktoberfest Beers, Scottish Ales, and darker beers including Stouts and Porters', 'Extract', 'http://www.brewingwithbriess.com/Assets/PDFs/Briess_PISB_CBWSparklingAmberLME.pdf', '0.000', '0.0', '0.0', '0.0', '0.0', '2012-02-05 07:51:34');
INSERT INTO `grains` VALUES ('105', 'CBW Porter LME', '17.0', '1.036', '0', '0', 'Briess', 'USA', '• CBW Porter LME is 100% pure malted barley extract made from a blend of 100% malt and water.\n• Many styles of extract beer, and to adjust the color, flavor and gravity of all grain beers\n• Use in the production of porters, stouts and other dark colored beers\n\nAvailable only as LME.', 'Extract', 'http://www.brewingwithbriess.com/Assets/PDFs/Briess_PISB_CBWPorterLME.pdf', '0.000', '0.0', '0.0', '0.0', '0.0', '2012-02-05 07:54:40');
INSERT INTO `grains` VALUES ('104', 'CBW Sparkling Amber DME', '4.0', '1.045', '0', '0', 'Briess', 'USA', '• CBW Sparkling Amber DME is 100% pure malted barley extract made from a blend of 100% malt and water\n• Use in the production of Pale Ales, Red and Amber Ales, Munich Style Beers, Bock and Oktoberfest Beers, Scottish Ales, and darker beers including Stouts and Porters', 'Extract', 'http://www.brewingwithbriess.com/Assets/PDFs/Briess_PISB_CBWSparklingAmberDME.pdf', '0.000', '0.0', '0.0', '0.0', '0.0', '2012-02-05 07:59:02');
INSERT INTO `grains` VALUES ('107', 'CBW Traditional Dark DME', '2.0', '1.045', '0', '0', 'Briess', 'USA', '• CBW Traditional Dark DME is a 100% pure malted barley extract made from a blend of 100% malts and water\n• Use in the production of Red and Amber Beers, Oktoberfest, Bock beer, Doppelbock, brown ales and other dark\ncolored beers', 'Extract', 'http://www.brewingwithbriess.com/Assets/PDFs/Briess_PISB_CBWTradDarkDME.pdf', '0.000', '0.0', '0.0', '0.0', '0.0', '2012-02-05 07:59:14');
INSERT INTO `grains` VALUES ('109', 'Cara 8', '8.0', '1.027', '0', '1', 'Dingemans', 'Belgium', 'The Cara 8  is a very light crystal/caramel malt. The malt is produced in the drum. Due to the  caramelisation and low drying temperatures its specific taste is achieved. Adding this type of malt increases the foam stability of your beers. Trough it’s light colour it can even be added in low percentages in your Pilsen beer to improve the foamstability.\n\nAKA Cara 20 in BE', 'Grain', 'http://www.dingemansmout.be/sites/dingemansmout.be/files/downloads/CARA_20_MD.pdf', '9.500', '65.0', '0.0', '0.0', '11.0', '2012-02-21 20:31:57');
INSERT INTO `grains` VALUES ('110', 'Cara 20', '20.0', '1.030', '0', '1', 'Dingemans', 'Belgium', 'The Cara 20 is a very light crystal/caramel malt use by a lot of Belgian breweries in producing Abbey beers. The malt is produced in the drum. Due to the caramelisation and low drying temperatures its specific taste is achieved. \n\nAKA Cara 50 in BE', 'Grain', 'http://www.dingemansmout.be/sites/dingemansmout.be/files/downloads/CARA%2050%20MD.pdf', '7.000', '70.0', '0.0', '0.0', '11.0', '2012-02-21 20:31:57');
INSERT INTO `grains` VALUES ('111', 'Cara 45', '45.0', '1.031', '0', '1', 'Dingemans', 'Belgium', 'The Cara 45  is a darker caramel malt use by a lot of Belgian breweries in producing Abbey beers. The malt is produced in the drum. The malt will impart a rich caramel-sweet arome and full flavor, as well as intense dark color.\n\nAKA Cara 120 in BE', 'Grain', 'http://www.dingemansmout.be/sites/dingemansmout.be/files/downloads/CARA_120_MD_0.pdf', '5.500', '70.0', '0.0', '0.0', '11.0', '2012-02-21 20:31:57');
INSERT INTO `grains` VALUES ('112', 'Roasted Wheat Malt', '12.0', '1.035', '0', '1', 'Dingemans', 'Belgium', 'This toasted wheat malt is slightly toasted. Therefore it has a bread-like, nutty flavor.\n\nDark special beers, Stout, Dark Ales, bock beers\n\n\n', 'Grain', 'http://www.dingemansmout.be/sites/dingemansmout.be/files/downloads/WHEAT_MD.pdf', '6.500', '80.0', '0.0', '0.0', '11.0', '2012-02-21 20:31:57');
INSERT INTO `grains` VALUES ('113', 'Amber', '36.0', '1.032', '0', '1', 'Thomas Fawcett', 'England', 'Amber malt is a more toasted form of pale ale malt and is typically used in Brown Porter recipes. This malt has no diastatic power.\n\nAmber (30-42L)', 'Grain', '', '3.000', '0.0', '71.0', '0.0', '0.0', '2012-02-05 19:40:38');
INSERT INTO `grains` VALUES ('114', 'Black Malt', '455.0', '1.032', '0', '1', 'Thomas Fawcett', 'England', 'Black malt is barley malt that has been roasted to the point of carbonizing. This malt provides intensely dark color and acrid, ashy, roasted undertones to the flavor of beer.\n\nBlack Malt (410-500L)', 'Grain', '', '3.000', '0.0', '71.0', '0.0', '0.0', '2012-02-05 19:42:47');
INSERT INTO `grains` VALUES ('115', 'Brown Malt', '58.0', '1.032', '0', '1', 'Thomas Fawcett', 'England', 'Brown malt is a darker form of pale malt and is typically used in brown ale as well as in porter and stout.\n\nBrown Malt (52-65L)', 'Grain', '', '3.000', '0.0', '71.0', '0.0', '0.0', '2012-02-05 19:43:23');
INSERT INTO `grains` VALUES ('116', 'CaraMalt', '23.5', '1.033', '0', '1', 'Thomas Fawcett', 'England', 'Will impart a light flavor and slight red hue. Great for dark lagers and ales. Will contribute dextrins and add to foam stability.\n\nCaraMalt (20-27L)\n', 'Grain', '', '7.000', '0.0', '77.0', '0.0', '0.0', '2012-02-05 19:44:29');
INSERT INTO `grains` VALUES ('117', 'Chocolate Malt', '365.0', '1.032', '0', '1', 'Thomas Fawcett', 'England', 'A classic malt for stout, porter, and brown ale. Provides color for a wide variety of darker beer styles.\n\nChocolate Malt (325-400L)', 'Grain', '', '3.000', '0.0', '71.0', '0.0', '0.0', '2012-02-05 19:45:06');
INSERT INTO `grains` VALUES ('118', 'Crystal Malt I', '45.0', '1.034', '0', '1', 'Thomas Fawcett', 'England', 'A good general-purpose crystal malt. Will impart a moderate amber color and caramel flavor to your beer.\n\nCrystal Malt I (42-48L)', 'Grain', '', '4.500', '0.0', '77.0', '0.0', '0.0', '2012-02-05 19:45:28');
INSERT INTO `grains` VALUES ('119', 'Crystal Malt II', '66.0', '1.034', '0', '1', 'Thomas Fawcett', 'England', 'An excellent all-purpose crystal malt. Will impart sweet caramel/crystal/toffee notes to your beer.\n\nCrystal Malt II (62-68L)', 'Grain', '', '4.500', '0.0', '77.0', '0.0', '0.0', '2012-02-05 19:45:57');
INSERT INTO `grains` VALUES ('120', 'Crystal Rye', '75.0', '1.031', '0', '1', 'Thomas Fawcett', 'England', 'Will lend a dry, licorice, toffee flavor. Great for use in complex, multi-grain brews where that extra special something is required.\n\nCrystal Rye (70-80L)', 'Grain', '', '4.500', '0.0', '70.0', '0.0', '0.0', '2012-02-05 19:46:19');
INSERT INTO `grains` VALUES ('121', 'Crystal Wheat', '54.0', '1.032', '0', '1', 'Thomas Fawcett', 'England', 'Caramel wheat malt may be used just like other forms of caramel and crystal malt to emphasize wheat malt flavor and aroma and add color to wheat beers.\n\nCrystal Wheat (50-58L)', 'Grain', '', '4.500', '0.0', '72.0', '0.0', '0.0', '2012-02-05 19:46:52');
INSERT INTO `grains` VALUES ('122', 'Dark Crystal Malt I', '86.5', '1.034', '0', '1', 'Thomas Fawcett', 'England', 'Contributes a strong reddish hue to dark beers. This malt will impart a roasty edge on top of a malty sweetness.\n\nDark Crystal Malt I (83-90L)', 'Grain', '', '4.500', '0.0', '77.0', '0.0', '0.0', '2012-02-05 19:47:19');
INSERT INTO `grains` VALUES ('123', 'Dark Crystal Malt II', '121.0', '1.034', '0', '1', 'Thomas Fawcett', 'England', 'Will provide intense, crystal malt sweetness coupled with a moderate amount of burnt caramel bitterness. Use in moderation.\n\nDark Crystal Malt II (118-124L)', 'Grain', '', '4.500', '0.0', '77.0', '0.0', '0.0', '2012-02-05 19:50:35');
INSERT INTO `grains` VALUES ('124', 'Golden Promise', '2.7', '1.036', '0', '1', 'Thomas Fawcett', 'England', 'Golden Promise is a traditional Pale Ale Malt grown in Scotland. It produces sweet, mellow wort and is excellent for both brewing and distilling.\n\nGolden Promise (2.3-3.0L)', 'Grain', '', '3.000', '0.0', '80.0', '0.0', '0.0', '2012-02-05 19:50:16');
INSERT INTO `grains` VALUES ('125', 'Halcyon', '2.7', '1.036', '0', '1', 'Thomas Fawcett', 'England', 'Halcyon is a barley variety that was bred from the Maris Otter and Sargent barley varieties. It tends to produce a less sweet wort than Maris Otter.\n\nHalcyon (2.3-3.0L)', 'Grain', '', '3.000', '0.0', '80.0', '0.0', '0.0', '2012-02-05 19:51:01');
INSERT INTO `grains` VALUES ('126', 'Maris Otter', '2.7', '1.036', '0', '1', 'Thomas Fawcett', 'England', 'Maris Otter has long been the favorite of English brewers for its brewhouse performance and rich, slightly nutty flavor. An excellent malt for any English beer style. All of Fawcetts Maris Otter malt is still produced in their traditional floor maltings.\n\nMaris Otter (2.3-3.0L)', 'Grain', '', '3.000', '0.0', '80.0', '0.0', '0.0', '2012-02-05 19:51:17');
INSERT INTO `grains` VALUES ('127', 'Oat Malt', '2.1', '1.031', '0', '1', 'Thomas Fawcett', 'England', 'Great for enhancing body and flavor of dark beers. Use at a rate of 5  10%.\n\nOat Malt (1.9-2.3L)', 'Grain', '', '4.000', '0.0', '70.0', '0.0', '0.0', '2012-02-05 19:51:33');
INSERT INTO `grains` VALUES ('128', 'Optic', '2.7', '1.036', '0', '1', 'Thomas Fawcett', 'England', 'Optic is a barley variety widely grown in the United Kingdom. Our customers have been impressed with its highly aromatic qualities.\n\nOptic (2.3-3.0L)', 'Grain', '', '3.000', '0.0', '80.0', '0.0', '0.0', '2012-02-05 19:54:34');
INSERT INTO `grains` VALUES ('129', 'Pale Chocolate Malt', '217.5', '1.032', '0', '1', 'Thomas Fawcett', 'England', 'Less highly-roasted than regular chocolate malt. Will add dark brown color and a mild chocolate/coffee flavor to dark milds, stouts, and porters.\n\nPale Chocolate Malt (185-250L)', 'Grain', '', '3.000', '0.0', '71.0', '0.0', '0.0', '2012-02-05 19:55:01');
INSERT INTO `grains` VALUES ('130', 'Pale Crystal', '23.5', '1.033', '0', '1', 'Thomas Fawcett', 'England', 'Imparts a light caramel/crystal flavor, and slight red hue. A great crystal malt on the lower side of the color scale.\n\nPale Crystal (20-27L)', 'Grain', '', '7.000', '0.0', '77.0', '0.0', '0.0', '2012-02-05 19:55:16');
INSERT INTO `grains` VALUES ('131', 'Pearl', '2.7', '1.036', '0', '1', 'Thomas Fawcett', 'England', 'A traditional English barley variety known for its slightly bready flavor.\n\nPearl (2.3-3.0L)', 'Grain', '', '3.000', '0.0', '80.0', '0.0', '0.0', '2012-02-05 19:54:16');
INSERT INTO `grains` VALUES ('132', 'Roasted Barley', '455.0', '1.031', '0', '1', 'Thomas Fawcett', 'England', 'Made from barley that has not been malted. A distinct, more astringent roasted bitter flavor in dark beer and the perfect grain for Irish Stout.\n\nRoasted Barley (410-500L)', 'Grain', '', '3.000', '0.0', '70.0', '0.0', '0.0', '2012-02-05 19:55:33');
INSERT INTO `grains` VALUES ('133', 'Roasted Wheat', '380.0', '1.032', '0', '1', 'Thomas Fawcett', 'England', 'Intensifies the typical aroma and color of dark top fermented beers such as Altbier, Weizen and Weizenbock.\n\nRoasted Wheat (360-400L)', 'Grain', '', '5.000', '0.0', '73.0', '0.0', '0.0', '2012-02-05 19:55:47');
INSERT INTO `grains` VALUES ('134', 'Rye Malt', '2.7', '1.036', '0', '1', 'Thomas Fawcett', 'England', 'Will add a dry, spicy note to your finished beer. Best used in small quantities (less than 15%) to avoid gumming up your mash.\n\nRye Malt (2.5-3.0L)', 'Grain', '', '3.000', '0.0', '80.0', '0.0', '0.0', '2012-02-05 19:56:06');
INSERT INTO `grains` VALUES ('135', 'Wheat Malt', '2.1', '1.037', '0', '1', 'Thomas Fawcett', 'England', 'An excellent UK wheat malt which produces superb wheat beers with aromas appropriate for the style.\n\nWheat Malt (1.9-2.3L)', 'Grain', '', '4.000', '0.0', '82.0', '0.0', '0.0', '2012-02-05 19:56:23');
