INSERT INTO `hops` VALUES ('44', 'Aurora', '8.3', '8.3', '23.0', 'Slovenia', '', '', '', '', 'Also called Super Styrian.  This is a hybrid between Northern Brewer and TG.  It has a pleasant hoppy aroma similar to Styrian Goldings but lighter.  \nUsed for: Often mixed with other hops in lagers.  \nSubstitutes: Styrian Goldings', '', '0', '1', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('45', 'Banner', '10.0', '10.0', '35.0', 'USA', '', '', '', '', 'Bittering hops developed in 1988.  Use largely discontinued due to poor storage.\nUsed for: U.S. style ales\nSubstitutes: Cluster', '', '0', '0', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('46', 'Bobek', '5.3', '5.3', '20.0', 'Slovenia', '', '', '', '', 'Also called Styrian Golding B - a cross between Northern Brewer and a TG seedling.  Pleasant hop aroma and flavor.  \nUsed for: Moderate English ales and lagers/Pilsners \nSubstitutes: Northern Brewer', '', '0', '1', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('47', 'Bravo', '15.5', '15.5', '30.0', 'USA', '', '', '', '', 'A second generation super high alpha hops from the Hopsteiner breeding program released in 2006.  Good bittering hop.\nAroma: Fruity and floral aroma.\nSubstitutes: Columbus, Zeus, Tomahawk', '', '0', '0', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('48', 'Celeia', '4.5', '4.5', '23.0', 'Slovenia', '', '', '', '', 'A dual purpose hops that is a hybrid between Savinjski Golding, Aurora and Slovenian wild hops.  Similar in aroma to Saaz.  \nUsed for: Light lagers, Pilsners, Belgian ales.\nSubstitutes: Saaz, Styrian Goldings', '', '0', '1', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('49', 'Challenger', '7.5', '7.5', '25.0', 'England', '', '', '', '', 'Variant of Northern Brewers heritage\nUsed for: Both aroma and bittering for ales and lagers\nAroma: Fruity in flavor, spicy aroma, blends well\nSubstitutes: N/A', '', '0', '1', '0', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('50', 'Chelan', '13.3', '13.3', '20.0', 'USA', '', '', '', '', 'High alpha variety from the Haas breeding program released in 2004.  It is a variant of Galena and very similar in profile to Galena.\nAroma: Pleasant citrus notes  \nSubstitutes: Galena', '', '0', '0', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('51', 'Citra', '12.0', '12.0', '25.0', 'USA', '', '', '', '', 'Special aroma hops released in 2007.  Imparts high alpha/oil content but low cohumulone.\nAroma: Adds interesting citrus and tropical fruit character to the beer.  \nSubstitutes: Unknown', '', '0', '1', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('52', 'Columbia', '5.5', '5.5', '35.0', 'England', '', '', '', '', 'Sibling of Williamette hops.\nUsed for: All English Ales\nAroma: Close to Fuggles\nSubstitutes: Fuggles, Williamette', '', '0', '0', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('53', 'Columbus (Tomahawk)', '14.0', '14.0', '40.0', 'USA', '', '', '', '', 'Engineered Centennial Substitute - High alpha bittering hops.\nUsed for: Bittering, flavor, aroma - IPA, American Pale Ale, Stout, Lager\nAroma: Pungent, spicy. Strongly aromatic, but clean tasting bitterness\nSubstitutes: Centennial', '', '0', '0', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('54', 'Comet', '9.5', '9.5', '50.0', 'USA', '', '', '', '', 'Very bitter hops - use sparingly with other hops\nUsed for: Bittering only\nSubstitutes: N/A', '', '0', '0', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('55', 'Feux Coeur Francais', '14.0', '14.0', '25.0', 'Australia', '', '', '', '', 'Rare, high alpha hops from Australia with genetic roots in the Burgundy region of France.  It was adopted to the Victoria region of Australia and introduced in 2010.', '', '0', '0', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('56', 'First Gold', '7.5', '7.5', '12.0', 'England', '', '', '', '', 'Dwarf hop with English Golding character\nUsed for: Ales, ESB\nAroma: Similar to other Golding varieties, spicy\nSubstitutes: East Kent Goldings, Crystal', '', '0', '1', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('57', 'Galaxy', '14.0', '14.0', '35.0', 'Australia', '', '', '', '', 'A high alpha, dual purpose seedless hops with a unique hop aroma.  It can be used either for bittering or as a late hop addition to provide a flavor of citrus and passionfruit to the finished beer.\nAroma: Intense, pleasant, unique.', '', '0', '0', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('58', 'Glacier', '5.6', '5.6', '28.5', 'USA', '', '', '', '', 'Genetic mix of at least 8 hops including Elsasser, Brewers Gold, N Brewer, Bullion, Early Green, and others...released in 2000.\nUsed for: Aroma\nAroma: Excellent, hoppy\nSubstitutes: N/A', '', '0', '1', '0', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('59', 'Goldings, B.C.', '5.0', '5.0', '40.0', 'Canada', '', '', '', '', 'Used for: Bittering and finishing British ales, bitters, porters and stouts.\nAroma: Spicy, floral, rounded mild aroma.\nSubstitutes: East Kent Goldings, Fuggles', '', '0', '1', '0', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('60', 'Goldings, East Kent', '5.0', '5.0', '35.0', 'England', '', '', '', '', 'Used for: General purpose hops for bittering/finishing all British Ales\nAroma: Floral, aromatic, earthy, slightly sweet spicy flavor\nSubstitutes: Fuggles, BC Goldings\nExamples: Bass Pale Ale, Fullers ESB, Samual Smith\'s Pale Ale', '', '0', '1', '0', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('61', 'Greenburg', '5.2', '5.2', '25.0', 'USA', '', '', '', '', 'American hop variety from southern Idaho.  Fruity flavor with a touch of wood flavor.  Popular with microbreweries.', '', '0', '1', '0', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('62', 'Hallertauer Hersbrucker', '4.0', '4.0', '25.0', 'Germany', '', '', '', '', 'Used for: German style lagers and wheat beers\nAroma: Noble, pleasant, clean flavor\nSubstitutes: Mt. Hood, Liberty, Hallertauer, Mittelfrueh\nExamples: Wheathook Wheaten Ale', '', '0', '1', '0', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('63', 'Hallertauer Mittelfrueh', '4.0', '4.0', '45.0', 'Germany', '', '', '', '', 'Used for: Continental Lagers, German Ales/Lagers, US Lagers\nAroma: Pleasant, assertive, mild herbal aroma\nSubstitutes: Hallertauer, Mt. Hood, Liberty, Crystal\nExamples: Sam Adams Boston Lager, Sam Adams Boston Lightship', '', '0', '1', '0', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('64', 'Hallertauer, New Zealand', '8.5', '8.5', '45.0', 'New Zealand', '', '', '', '', 'Hallertauer variant with classic aroma and ability to retain clean taste. Bred from German Hallertauer Mittelfrueh\nUsed for: Australian Ales\nSubstitutes: Hallertauer Mittelfrueh', '', '0', '1', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('65', 'Herald', '12.0', '12.0', '25.0', 'England', '', '', '', '', 'Dwarf variety bred at Wye College (mid 1990s)\nUsed for: Bittering of ales\nAroma: Strong, acceptable\nSubstitutes: High alpha English bittering hops', '', '0', '0', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('66', 'Horizon', '12.0', '12.0', '30.0', 'USA', '', '', '', '', 'Good dual purpose hops derived from Nugget hops in Oregon (1970)\nUsed for: All purpose Ales, Lagers\nAroma: Pleasant, hoppy\nSubstitutes: Magnum or other high alpha hops', '', '0', '0', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('67', 'Magnum', '14.0', '14.0', '15.0', 'Germany', '', '', '', '', 'German Hallertauer hybrid, widely used in Germany\nUsed for: German ales and lagers\nAroma: Good aroma and stable spicy flavor\nSubstitutes: N/A', '', '0', '0', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('68', 'Marynka', '10.5', '10.5', '25.0', 'Poland', '', '', '', '', 'Bittering hops with high aroma.  Primarily for use in Pilsner, lagers, ales or wheats.  Comparable to English Goldings or Chinook.  \nUsed for: Lager, ales\nSubstitutes: Chinook, English Golding', '', '0', '0', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('69', 'Millenium', '14.3', '14.3', '25.0', 'USA', '', '', '', '', 'Originally developed as part of the Haas breeding program in the US, Millenium entered production in 2000.  It is a very high alpha hops used for bittering.\nAroma: Mild, herbal\nSubstitutes: Columbus, Tomahawk, Zeus and Nugget', '', '0', '0', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('70', 'Motueka', '7.0', '7.0', '15.0', 'New Zealand', '', '', '', '', 'Flexible hop developed from Saaz.  Has notable character and aroma, balanced bitterness, and unique flavor.  \nUsed for: Lagers, Belgian Ales and Bohemian Pilsner.  \nSubstitutes: Saaz', '', '0', '1', '0', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('71', 'Nelson Sauvin', '12.0', '12.0', '20.0', 'New Zealand', '', '', '', '', 'Triploid variety bred from \"Smoothcone\" released in 2000.  Oil profile said to taste like \"fresh crushed gooseberries\" similar to Sauvignon Blanc, giving it its name.  Very fruity, citrusy - use in moderation.  \nUsed for: Pale ale, Super Premiums.  \nSubstitutes: None', '', '0', '0', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('72', 'Newport', '13.5', '13.5', '40.0', 'USA', '', '', '', '', 'High alpha hops released in 2002 as a potential replacement for Galena.  Note that these hops have a large alpha range of 9.8%-17% so check the package!  Fairly pungent.\nUsed for: Primarily used for bittering in ales, stouts, barley wine.  \nSubstitutes: Galena', '', '0', '0', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('73', 'Northdown', '8.5', '8.5', '15.0', 'England', '', '', '', '', 'Variation of Northern Brewers hops grown in England.\nUsed for: Bittering in English ales\nAroma: Good bittering, flavor and aroma\nSubstitutes: Northern Brewer', '', '0', '1', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('74', 'Opal', '6.5', '6.5', '25.0', 'Germany', '', '', '', '', 'New variety from the hop research center in Hull.\nUsed for: Aroma hops in continental styles\nAroma: High aroma hops with hints of spice and subtle citrus.\nSubstitutes: Unknown', '', '0', '1', '0', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('75', 'Orion', '7.3', '7.3', '17.0', 'Germany', '', '', '', '', 'Cross between Perle and an experimental variety\nUsed for: Bittering and aroma\nAroma: Pleasant, hoppy\nSubstitutes: Perle', '', '0', '1', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('76', 'Pacific Gem', '15.0', '15.0', '10.0', 'New Zealand', '', '', '', '', 'High alpha smoothcone cross developed at DSIR in 1987.\nUsed for: Bittering - produces a cask-oak woody flavor.\nAroma: Pleasant - some blackberry aroma.\nSubstitutes: Bullion', '', '0', '0', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('77', 'Pacific Jade', '13.0', '13.0', '15.0', 'New Zealand', '', '', '', '', 'High alpha bittering hops that delivers a bold herbal infusion of fresh citrus with some black pepper flavor.  Citrus aroma and flavor tempers ales and may be used as a finishing hops. \nUsed for: Ales, lagers \nSubstitutes: N/A', '', '0', '0', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('78', 'Pacifica', '5.5', '5.5', '20.0', 'New Zealand', '', '', '', '', 'Aroma hop developed from Hallertauer Mittelfrueh in New Zealand (1994).  Blend of old and new world taste including Orange marmalade and citrus aroma notes when used in late addition.  Soft finish if used in bittering.  \nSubstitutes: Chinook, Hallertauer Mittelfrueh', '', '0', '1', '0', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('79', 'Palisade', '7.5', '7.5', '15.0', 'USA', '', '', '', '', 'Also called YCR-4, Palisade is an aroma variety bread by Yakima Chief Ranches.  It has both aromatic and moderate bittering properties.  \nSubstitutes: Willamette', '', '0', '1', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('80', 'Phoenix', '8.0', '8.0', '10.0', 'England', '', '', '', '', 'Seedling derived from Wye Yoeman.\nUsed for: Ales\nAroma: Similar to Wye Challenger hops\nSubstitutes: Challenger, East Kent Golding, Northdown', '', '0', '0', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('81', 'Pilgrim', '11.5', '11.5', '18.0', 'England', '', '', '', '', 'New variety from Wye College in 2001.  High Selinene content.\nUsed for: Bittering\nSubstitutes: N/A', '', '0', '0', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('82', 'Pilot', '11.5', '11.5', '25.0', 'England', '', '', '', '', 'A very new hops introduced in 2002 by Charles Faram & Co Ltd.  Mild herbal flavor with lemon hints and fresh mowed grass flavor.  \nUsed for: Primarily for bittering in APAs.  \nRare.', '', '0', '0', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('83', 'Pioneer', '9.0', '9.0', '17.0', 'England', '', '', '', '', 'Bred from Wye Omega at Wye College in the UK\nUsed for: ESB, English Ales\nAroma: Mild English hop aroma\nSubstitutes: East Kent Goldings', '', '0', '1', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('84', 'Premiant', '7.5', '7.5', '25.0', 'Czech Republic', '', '', '', '', 'A cross between Czech aroma and bittering varieties.  Dual purpose though its aroma is not as strong as some other Czech hops.  Use in beers where a moderate hoppy aroma is needed.  \nUsed for: Moderate ales, lagers \nSubstitutes: Saaz', '', '0', '1', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('85', 'Progress', '6.3', '6.3', '15.0', 'England', '', '', '', '', 'Used for: Bittering and aroma in English Ales\nAroma: Similar to Fuggles, aromatic, slightly sweet\nSubstitutes: Fuggles', '', '0', '1', '0', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('86', 'Riwaka', '5.3', '5.3', '15.0', 'New Zealand', '', '', '', '', 'Formerly called B-Saaz, this was developed from old line Saazer hops in New Zealand.  \nUsed for: New world pale ale, regional pilsners\nAroma: Strong, hoppy. Some grapefruit and citrus character.', '', '0', '1', '0', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('87', 'Santiam', '6.0', '6.0', '35.0', 'USA', '', '', '', '', 'Triploid aroma selection derived from Tettnang and Hallertauer (1997)\nUsed for: Lager, U.S. Pale Ale, Pilsner\nSubstitutes: German Tettnang, Spalt, Spalt Select', '', '0', '1', '0', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('88', 'Saphir', '3.5', '3.5', '45.0', 'Germany', '', '', '', '', 'A new noble breed as an alternative to Hallertau Mittelfrueh in an attempt to make it more disease resistant and commercially viable.  Similar in character to Hallertau varieties.  \nUsed for: Pilsners, lagers, Belgian whites.  \nSubstitutes: Hallertau Mittelfrueh', '', '0', '1', '0', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('89', 'Satus', '13.3', '13.3', '33.0', 'USA', '', '', '', '', 'High alpha bittering hops from Yakima that is similar to Galena.  Of recent origin.  \nSubstitutes: Galena', '', '0', '0', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('90', 'Select Spalt', '4.8', '4.8', '17.0', 'Germany', '', '', '', '', 'Bred from Hallertauer Mittelfrueh and Spalt in Germany.  Spalt character.\nUsed for: Lager, Pilsner, Noble aroma hop substitute\nAroma: Very fine, spalt aroma\nSubstitutes: Spalter, Saaz, Tettnang', '', '0', '1', '0', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('91', 'Simcoe', '13.0', '13.0', '18.0', 'USA', '', '', '', '', 'High alpha bittering hops with good aroma characteristics.\nUsed for: IPAs\nAroma: Citrus and pine-like aroma\nSubstitutes: N/A', '', '0', '0', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('92', 'Sladek', '5.0', '5.0', '25.0', 'Czech Republic', '', '', '', '', 'Hybrid variety of Saaz origin with noble hoppy taste and aroma.  Generally recommended for middle-hopping about 20 min before end of boil.  Not a replacement for Saaz, but complimentary in Lagers.  \nUsed for: Lager \nSubstitutes: Saaz', '', '0', '1', '0', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('93', 'Sorachi Ace', '12.0', '12.0', '25.0', 'Japan', '', '', '', '', 'Developed by Sapporo Breweries and used in their beers - has a Brewer\'s gold and Saaz heritage.  Reported to be lemony in flavor and aroma.  \nUsed for: Saison, IPA or other beer where lemon/citrus is desirable.  \nSubstitutes: Admiral, Cascade', '', '0', '0', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('94', 'Southern Cross', '13.0', '13.0', '15.0', 'New Zealand', '', '', '', '', 'Dual purpose with \"European\" flavor - from DSIR in 1994\nUsed for: European flavor bittering/aroma\nAroma: Spicy, lemony, slight pine and wood flavors\nSubstitutes: N/A', '', '0', '1', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('95', 'Spalter', '4.5', '4.5', '50.0', 'Germany', '', '', '', '', 'Used for: Traditional German bittering and aroma hops, Altbiers, Lagers\nAroma: Mild, pleasant, slight spice\nSubstitutes: Saaz, Tettnang\nExamples: Dusseldorf Altbiers', '', '0', '1', '0', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('96', 'Sterling', '7.5', '7.5', '30.0', 'USA', '', '', '', '', 'Flavor is a cross between Saaz and Mt Hood\nUsed for: Lagers, Ales, Pilsners\nAroma: Herbal, spicy, some floral and citrus hints\nSubstitutes: Saaz, Polish Lublin', '', '0', '1', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('97', 'Sticklebract', '13.5', '13.5', '17.0', 'New Zealand', '', '', '', '', 'High alpha hops from DSIR research first released in 1972.  Some of the same qualities as Northern Brewer\nUsed for: Dual purpose\nAroma: Pine-like with a hint of citrus\nSubstitutes: Northern Brewer', '', '0', '1', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('98', 'Strisslespalt', '4.0', '4.0', '35.0', 'France', '', '', '', '', 'Aroma hops used widely in the Alsace area of France.\nUsed for: Pilsner, lager, wheat\nAroma: Medium intensity, pleasant, hoppy\nSubstitutes: Mt Hood, Crystal, Hallertauer Hersbruck', '', '0', '1', '0', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('99', 'Summit', '17.0', '17.0', '15.0', 'USA', '', '', '', '', 'High alpha variety bred by American Dwarf Hop Assoc.  Can be grown commercially on a low trellis.  \nAroma: Strong citrus, grapefruit notes.\nSubstitutes: Warrior, Millenium, Columbus, Tomahawk, Zeus', '', '0', '0', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('100', 'Sun', '14.0', '14.0', '25.0', 'USA', '', '', '', '', 'A very high alpha bittering hops from Yakima Valley, Washington\nUsed for: Bittering (intense)\nAroma: Pleasant, hoppy\nSubstitutes: Other very high alpha US hops', '', '0', '0', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('101', 'Super Galena', '14.5', '14.5', '20.0', 'USA', '', '', '', '', 'Super high alpha hops from Hopsteiner breeding program released in 2006.  High alpha and beta content - comparable to Galena in bitterness and aroma profile.  \nUsed for: Primarily for bitterness.  \nSubstitutes: Galena', '', '0', '0', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('102', 'Super Pride', '14.3', '14.3', '45.0', 'Australia', '', '', '', '', 'A high alpha variation of the Pride of Ringwood hop variety.  Features mild aromas, higher alpha content and low cohumulone levels.  \nUsed for: Primarily for bittering.  \nSubstitutes: Pride of Ringwood', '', '0', '0', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('103', 'Target', '11.0', '11.0', '50.0', 'England', '', '', '', '', 'Used for: English ales and lagers\nAroma: Robust aroma and flavor\nSubstitutes: East Kent Goldings', '', '0', '0', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('104', 'Tettnang', '4.5', '4.5', '40.0', 'Germany', '', '', '', '', 'Used for: German ales, lagers and wheat beer\nAroma: Noble, mild, fine, slightly spicy\nSubstitutes: Saaz, Spalt\nExamples: Sam Adams Octoberfest, Anderson Valley ESB', '', '0', '1', '0', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('105', 'Tillicum', '13.3', '13.3', '20.0', 'USA', '', '', '', '', 'High alpha and high beta hops developed though Haas breeding program and released in 1995.  Daughter variety of Galena and related to Chelan - so similar to both. \nSubstitutes: Galena, Chelan', '', '0', '0', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('106', 'Tomahawk', '15.5', '15.5', '48.0', 'USA', '', '', '', '', 'Part of the Columbus/Tomahak/Zeus (CTZ) group of super high alpha hops.  Together these make up 1/4 of US hop acreage.  \nUsed for: High alpha hops used primarily for commercial bittering.  \nSubstitutes: Tomahawk, Zeus, Nugget, Galena, Chinook', '', '0', '0', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('107', 'Topaz', '17.0', '17.0', '30.0', 'Australia', '', '', '', '', 'Very high alpha hop variety from Australia.  Used for commercial production primarily for its high alpha acid content and high bitterness.', '', '0', '0', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('108', 'Tradition', '6.0', '6.0', '10.0', 'Germany', '', '', '', '', 'Close descendant of Hallertauer Mittelfrueh bred for high yield.\nUsed for: Lager, Pilsner, Bock, some Weizens\nAroma: Fine - similar to Hallertauer Mittelfrueh\nSubstitutes: Hallertauer Mittelfrueh, Liberty, Ultra', '', '0', '0', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('109', 'Ultra', '3.0', '3.0', '27.5', 'USA', '', '', '', '', 'Triploid seedling of the German Hallertauer Variety\nUsed for: Lager, Pilsner, wheat, finishing hops in ales\nAroma: Good to outstanding - some Saaz like qualities\nSubstitutes: Liberty, Hallertauer, Tradition, Saaz', '', '0', '1', '0', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('110', 'Vanguard', '5.5', '5.5', '18.0', 'USA', '', '', '', '', 'Diploid seedling made in 1982, released in 1997. Hallertau Mitterfrueh parentage.\nUsed for: European lagers, ales\nAroma: Strong aromatic notes\nSubstitutes: Hallertauer Mittelfrueh', '', '0', '1', '0', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('111', 'Victoria', '13.0', '13.0', '35.0', 'Australia', '', '', '', '', 'Hi alpha hops grown in Victoria, Australia.  It was created in 1976 and entered commercial production in the 1990s.\nUsed for: Primarily for bittering due to high alpha content.', '', '0', '0', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('112', 'Warrior', '15.0', '15.0', '24.0', 'USA', '', '', '', '', 'High alpha hops from Yakima Chief Ranches. Also called YCR-5.  Mild aroma and low cohumulone content - primarily for bittering.\nUsed for: Ales, stouts\nAroma: Neutral, clean, mild\nSubstitutes: Nugget, Columbus, Magnum', '', '0', '1', '1', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('113', 'Whitbread Golding Variety', '6.0', '6.0', '35.0', 'England', '', '', '', '', 'Derived in England in 1911 from Bates Brewer. Some Golding characteristics, but lacking the full Goldings aroma.\nUsed for: Ales\nAroma: Pleasant, hoppy, slightly intense\nSubstitutes: East Kent Goldings, Progress', '', '0', '1', '0', '0', '2012-02-21 17:52:55');
INSERT INTO `hops` VALUES ('114', 'Zeus', '14.0', '14.0', '45.0', 'USA', '', '', '', '', 'Super high alpha bittering hops developed in the Yakima Valley, WA\nUsed for: Bittering, intense aroma\nAroma: Aromatic, pleasant\nSubstitutes: Other high alpha hops', '', '0', '0', '1', '0', '2012-02-21 17:52:55');
