INSERT INTO `misc` VALUES ('1', 'Alpha-amylase', 'Ferment', 'W', 'Teaspoons', 'Ferment', 'Enzymatically converts starches to glucose at temperature less than 140F.  1 teaspoon (per 5 gallons) should influence conversion..', '', '2011-12-22 15:51:37');
INSERT INTO `misc` VALUES ('2', 'Ascorbic Acid', 'Ferment', 'W', 'Grams', 'Ferment', 'Aka Vitamin C.  Gives something for oxygen in the beer to react with rather than the beer itself.  1/2 teaspoon (per 5 gallons) dissolved in boiled water will help prevent oxidation.', '', '2011-12-22 15:51:37');
INSERT INTO `misc` VALUES ('4', 'Calcium Carbonate / Chalk', 'Water', 'W', 'Grams', 'Setup', 'Food grade calcium carbonate (CaCO3), also known as chalk is used by grain brewers to adjust mash pH upward and also used to simulate the waters found where great stouts and porters are brewed.', '', '2011-12-22 15:51:37');
INSERT INTO `misc` VALUES ('5', 'Calcium Chloride', 'Water', 'W', 'Grams', 'Setup', 'Food grade calcium chloride (CaCl) is used to add chloride content and/or lower mash pH without increasing sodium as would be the case if sodium chloride or gypsum was used.', '', '2011-12-22 15:51:37');
INSERT INTO `misc` VALUES ('6', 'Citric Acid', 'Flavor', 'W', 'Grams', 'Ferment', 'Used in meads to add a desirable acid flavor.', '', '2011-12-22 15:51:37');
INSERT INTO `misc` VALUES ('7', 'Gelatin', 'Fining', 'W', 'Grams', 'Ferment', 'Attracts and settles out suspended yeast after fermentation. Used mainly in kegging where there is a longer distanct for the yeast to settle. Add 1 tablespoon in 1 pint water and gently heat (no boiling) to disolve and add with priming sugar.', '', '2011-12-22 15:51:37');
INSERT INTO `misc` VALUES ('8', 'Calcium Sulfate / Gypsum', 'Water', 'W', 'Grams', 'Setup', 'Food grade gypsum (calcium sulfate - CaSO4), sometimes called \"Burton Salts\" is used by grain brewers to insure proper mash pH and improve extraction. Also used to simulate the highly gypseous water found in Burton-on-Trent, the birthplace of pale ales. Note that gypsum only lowers pH when mashed with grain.', '', '2011-12-22 15:51:37');
INSERT INTO `misc` VALUES ('10', 'Irish Moss', 'Fining', 'W', 'Grams', 'Boil', 'Actually a blend of select seaweeds, Irish Moss is used during the boil as a \"kettle coagulant\" to help proteins precipitate, resulting in clearer, haze-free beer. Contrary to popular belief, Irish Moss should be rehydrated for 30 minutes before adding to the boil if added in the last 30 minutes. An easier alternative is to add it with the first bittering hops so that it is boiled at least 60 minutes, eliminating the need for rehydration. We recommend 1 tbs in a five gallon boil.', '', '2011-12-22 15:51:37');
INSERT INTO `misc` VALUES ('11', 'Koji', 'Ferment', 'W', 'Grams', 'Ferment', 'Enzymatically converts starches to glucose at temperatures between 100 and 120F.  1 teaspoon (per 5 gallons) should influence conversion.  Used to make sake.', '', '2011-12-22 15:51:37');
INSERT INTO `misc` VALUES ('13', 'Polycar', 'Fining', 'W', 'Grams', 'Ferment', 'This used to be called Polyclar AT or VT and is used in the last few days of fermentation to cause the proteins responsible for chill haze to precipitate. It needs to be rehydrated by soaking in sterile water for 1 hour and then gently stirred into the beer. Use 1 gm per gallon of beer. Then let the beer settle for a few days before bottling/kegging, racking off the sediment.', '', '2011-12-22 15:51:37');
INSERT INTO `misc` VALUES ('14', 'Silca Gel', 'Fining', 'W', 'Grams', 'Ferment', 'Brewery grade silica gel is used in a similar manner to and for the same purpose as Polyclar, to remove haze causing proteins, but our tests show it works much better. Gently stir into the beer, then let the beer settle for a few days before bottling/kegging, racking off the  sediment. Use 2 to 4 gms per gallon of beer.', '', '2011-12-22 15:51:37');
INSERT INTO `misc` VALUES ('15', 'Yeast Nutrient Crystals', 'Ferment', 'W', 'Grams', 'Ferment', 'Food grade di-ammonium phosphate acts like \"yeast fertilizer\" and is great for yeast starters (use 1/2 tsp) and for meads and ciders where natural yeast nutrients may be deficient.', '', '2011-12-22 15:51:37');
INSERT INTO `misc` VALUES ('16', 'Anise, Star', 'Spice', 'V', 'Kg', 'Boil', 'Star Anise spice adds a strong black licorice flavor to holiday and specialty beers.  Boil for 30 minutes to extract flavor.', '', '2012-02-21 22:24:03');
INSERT INTO `misc` VALUES ('17', 'Apricot Extract', 'Flavor', 'V', 'Kg', 'Package', 'Apricot flavor extract.  Mix into beer just before bottling.  Adds apricot flavor to light ales and wheat beers.  Use low hop rate to allow sweet flavor through.', '', '2012-02-21 22:24:03');
INSERT INTO `misc` VALUES ('18', 'Baking Soda', 'Water', 'W', 'Grams', 'Setup', 'Baking Soda (NaHCO3) may be used as a water mineral to adjust brewing water for mashing.', '', '2012-02-21 22:24:03');
INSERT INTO `misc` VALUES ('19', 'Blackberry Extract', 'Flavor', 'V', 'Kg', 'Package', 'Blackberry extract flavoring.  Mix into beer at bottling to add berry flavor to light ales and wheat beers.  Use low hop rate to allow sweet flavor through.', '', '2012-02-21 22:24:03');
INSERT INTO `misc` VALUES ('20', 'Burton Water Salts', 'Water', 'V', 'Grams', 'Setup', 'Also known as brewing salts, it is a mixture of Gypsum, Potassium Chloride and Epsom Salt.  Used to harden water.', '', '2012-02-21 22:24:03');
INSERT INTO `misc` VALUES ('21', 'Cherry Extract', 'Flavor', 'V', 'Kg', 'Package', 'Cherry extract flavoring.  Mix into beer at bottling to add cherry flavor to light ales and wheat beers.  Use low hop rate to allow sweet flavor through.', '', '2012-02-21 22:24:03');
INSERT INTO `misc` VALUES ('22', 'Cinnamon Stick', 'Spice', 'V', 'Kg', 'Boil', 'Used to add cinnamon flavor to holiday beers.  Goes well with ginger, honey and orange flavors.  Add one stick 5 minutes from the end of the boil.', '', '2012-02-21 22:24:03');
INSERT INTO `misc` VALUES ('23', 'Clearfine', 'Fining', 'V', 'Kg', 'Ferment', 'Commercial blend of animal collagen and polysaccarides that helps rapidly precipitate yeast and tannins.  Mix with 2 oz cold water and add after fermentation has completed.', '', '2012-02-21 22:24:03');
INSERT INTO `misc` VALUES ('24', 'Coriander Seed', 'Spice', 'V', 'Kg', 'Boil', 'Used in Belgian Wit, Whites, and Holiday ales.  Very good when used in light wheat ales.  Often used with Bitter Orange Peel.', '', '2012-02-21 22:24:03');
INSERT INTO `misc` VALUES ('25', 'Cranberry Extract', 'Flavor', 'V', 'Kg', 'Package', 'Cranberry flavor extract.  Add to beer just before bottling.  Adds distinctive cranberry flavor to light ales and wheat beers.  Use low hop rate to allow sweet flavor through.', '', '2012-02-21 22:24:04');
INSERT INTO `misc` VALUES ('26', 'Epsom Salt / MgSO4', 'Water', 'W', 'Grams', 'Setup', 'Water Treatment used to modify water profile.  Use the BeerSmith Water Profile tool to determine amount to add.', '', '2012-02-21 22:24:04');
INSERT INTO `misc` VALUES ('27', 'Sweet Gale', 'Spice', 'W', 'Kg', 'Boil', 'Very strong, fragrant shrub.  Great spice, though relatively unknown.  Aroma used in soups, stews, and candles.  Well suited for holiday beers.  Use sparingly -- the aroma and flavor are VERY strong.', '', '2012-02-21 22:24:04');
INSERT INTO `misc` VALUES ('28', 'Ginger Root', 'Spice', 'V', 'Kg', 'Boil', 'Adds distinctive ginger flavor for Ginger Ales, Holiday Beers.  Fresh grated ginger root from the grocer is best.  As little as 1/2 ounce per 5 gallons is noticable, but up to 4 oz can be used for ginger ales.', '', '2012-02-21 22:24:04');
INSERT INTO `misc` VALUES ('29', 'Isinglass', 'Fining', 'V', 'Kg', 'Ferment', 'Keep liquid form refrigerated.  Isinglass is widely used by commercial brewers to aid clarity, but is not as popular for home brewers due to stability.  Aids in settling proteins, tannins and yeast.', '', '2012-02-21 22:24:04');
INSERT INTO `misc` VALUES ('30', 'Lactic Acid', 'Water', 'V', 'Grams', 'Setup', 'Used to lower the pH of the mash without altering the water profile.  Lower pH mashes generally improves head retention, increases extraction rate.', '', '2012-02-21 22:24:04');
INSERT INTO `misc` VALUES ('32', 'Oak Chips', 'Flavor', 'V', 'Kg', 'Ferment', 'Can be added to the secondary or keg during aging to simulate oak flavor.  Boil first to sterilize and place in weighted bag so they do not float.  Amounts vary with chip size and time - smaller chips impart more flavor.', '', '2012-02-21 22:24:04');
INSERT INTO `misc` VALUES ('33', 'Oak Essence (Liquid)', 'Flavor', 'V', 'Kg', 'Package', 'Adds Oak Aged flavor to English Ales, IPAs.  Mix in prior to bottling.  Amouts vary with concentration, so check label or add to taste.', '', '2012-02-21 22:24:04');
INSERT INTO `misc` VALUES ('34', 'Orange Peel, Bitter', 'Spice', 'V', 'Kg', 'Boil', 'Made from the Curaco Orange Peel, peels are green-gray in color.  Used to add a distinctive flavor to Belgian Wits and White beers.', '', '2012-02-21 22:24:04');
INSERT INTO `misc` VALUES ('35', 'Orange Peel, Sweet', 'Spice', 'V', 'Kg', 'Boil', 'Adds a Conitreau or Grand Marnier flavor to your beer.  Used primarily in Belgian Strong Ale and Holiday Ales to add a sweet orange flavor and aroma.', '', '2012-02-21 22:24:04');
INSERT INTO `misc` VALUES ('36', 'Papain', 'Fining', 'W', 'Kg', 'Ferment', 'An enzyme extracted from papaya.  Helps reduce protein chill haze.  Add 0.5 gram at end of secondary fermentation, allow several days for it to work.  Available from herb and spice shops.', '', '2012-02-21 22:24:04');
INSERT INTO `misc` VALUES ('37', 'Raspberry Extract', 'Flavor', 'V', 'Kg', 'Package', 'Raspberry extract flavoring.  Mix into beer at bottling to add berry flavor to light ales and wheat beers.  Use low hop rate to allow sweet flavor through.', '', '2012-02-21 22:24:04');
INSERT INTO `misc` VALUES ('38', 'Salt', 'Water', 'W', 'Grams', 'Setup', 'Table Salt (NaCl) may be used as a water mineral to adjust brewing water for mashing.', '', '2012-02-21 22:24:04');
INSERT INTO `misc` VALUES ('39', 'Sassafras Root Bark', 'Spice', 'V', 'Kg', 'Boil', 'Bark is safe (do not use the tree or root - they may be toxic!).  This is the original source of the flavor for root beer, and adds a great root beer flavor.', '', '2012-02-21 22:24:04');
INSERT INTO `misc` VALUES ('40', 'Seeds of Paradise', 'Spice', 'W', 'Kg', 'Boil', 'Also called Paradise Seeds or Grains of Paradise. Native to west Africa.  Pepper zing with citrus notes, pine aroma.', '', '2012-02-21 22:24:04');
INSERT INTO `misc` VALUES ('41', 'Spruce Extract', 'Flavor', 'V', 'Kg', 'Package', 'Spruce essence generates the flavor and smell of fresh spruce pine needles.  Add in small increments and taste as it can be a rather strong flavoring.', '', '2012-02-21 22:24:04');
INSERT INTO `misc` VALUES ('42', 'Whirlfloc Tablet', 'Fining', 'V', 'Kg', 'Boil', 'Aids in clearing yeast and chill haze.  Easy to use tablet form.', '', '2012-02-21 22:24:04');
