INSERT INTO `recipedates` VALUES ('12', '8', '1', '1996-09-14', 'Brewed', '1.071', '0.0', 'Pitched not enough yeast. So heated back to 170F for 30 mins, chilled in sink and racked on top of big yeast cake. That got \'er goin\'.|11/12 Bottled 5 qts with 3.5 gm corn sugar.');
