INSERT INTO `recipedates` VALUES ('27', '16', '1', '1999-01-10', 'Brewed', '1.094', '0.0', 'Mini-mash 9 lbs @ 154F for 20 mins, boil for 30 mins. Mash in @ 140F for 20 mins, add decoct back to main mash and boost to 150F.Rest between 150 & 160 for 60 mins. Boil for 90 mins. Transfer ontop of yeast cake. Ferment active in hours.|1/11 - Spooge everywhere. Ferment at 66-68F.|2/13 - bottled.||Ferment went crazy with much blow off.');
