INSERT INTO `recipedates` VALUES ('96', '54', '1', '1996-03-02', 'Brewed', '1.044', '0.0', '9 qts 130f water added to grains & starch to rest @ 122F for 30 mins. added 6 qts boiling water to rest @ 150F for 30 mins. heatedto 158F and rested for 30 mins. sparged to 5.5 gallons with 180Fwater. Boiled for 1 hour down to 4.5 gallons. Added water back to make a volume of 6.5 gallons. Added yeast from the bottom of secondary of another batch. Racked off 1 gallon to send home with bro.');
