INSERT INTO `recipedates` VALUES ('188', '104', '1', '1998-06-10', 'Brewed', '1.100', '0.0', 'Mash in @ 151 for 30 mins. Plug in heater and let recirc for 45 mins climbing to 156F. Drain and boil for 90 mins. Chill to 70 andpitch rehydrated dry yeast. Oxgenate for 3 mins.|6/30 - Strong alcohol and esters. Some banana.');
