INSERT INTO `recipedates` VALUES ('246', '135', '1', '1999-02-20', 'Brewed', '1.056', '0.0', 'Mash in @ 154F for 15 mins.|2/22 - @ 50F.|2/23 - @ 45F|2/27 - @ 50F & 1022|3/1 - @ 50F & 1018|3/16 - Cane- 90 Grams in 6.0 Gallonsfor 2.53 vols @ 45.0F');
