INSERT INTO `recipedates` VALUES ('341', '193', '1', '2012-02-25', 'Brewed', '1.057', '60.0', 'Perfect mash, hit 154F, rest for 30 mins, circulate back to 154F and begin ramp to 168F at 45 mins. Drain at 60 mins, sparge to 12 gallons.');
