INSERT INTO `yeast` VALUES ('1', 'Altbier', '75', '70', 'Medium', 'Medium', 'Lager', 'Yeast Cult', 'A37', '', '', 'The strain used by many Alt breweries. Distinctive profile. Origin: Bavaria.', '', '45', '55', 'Fahrenheit', '2011-12-22 15:51:14');
INSERT INTO `yeast` VALUES ('2', 'American Ale', '73', '77', 'Medium-High', 'Medium', 'Ale', 'Wyeast', '1056', 'Liquid', '', 'Used commercially for several classic American ales. This strain ferments dry, finishes soft, smooth and dean, and is very well balanced.', '', '60', '72', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('3', 'American Ale', '70', '75', 'Medium', '', 'Ale', 'Yeast Cult', 'A01', '', '', 'Clean, crisp and neutral. Easy to use, forgiving. High attenuation, medium flocculation. A good all-purpose strain for all ales when little yeast character is desired, unobtrusive. The most popular brewpub strain. Styles: Barley wine, Brown ale, IPA, Pale ale. Origin: California, USA', '', '60', '75', 'Fahrenheit', '2011-12-22 15:51:14');
INSERT INTO `yeast` VALUES ('4', 'American Ale', '70', '75', 'Medium', 'Low', 'Ale', 'Yeast Labs', 'A02', '', '', 'This clean strain produces a very fruity aroma, with a soft and smooth flavor when fermented cool.  This is an all purpose ale yeast.  65-66F.', '', '60', '75', 'Fahrenheit', '2011-12-22 15:51:14');
INSERT INTO `yeast` VALUES ('5', 'American Ale II', '72', '76', 'Medium-High', 'High', 'Ale', 'Wyeast', '1272', 'Liquid', '', 'Fruitier and more flocculant than 1056, slightly nutty, soft, clean, slightly tart finish.', '', '60', '72', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('6', 'American Hefeweizen', '70', '75', 'Medium', 'Low', 'Wheat', 'White Labs', 'WLP320', 'Liquid', '', 'This yeast is used to produce the Oregon style American Hefeweizen. Unlike WLP300, this yeast produces a very slight amount of the banana and clove notes. It produces some sulfur, but is otherwise a clean fermenting yeast which does not Flocculate well, producing a cloudy beer.', '', '65', '69', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('7', 'American Lager', '75', '80', 'Medium-High', 'Medium', 'Lager', 'White Labs', 'WLP840', 'Liquid', '', 'This yeast is used to produce American style lagers. Dry and clean with a very slight apple fruitiness. Sulfur and diacetyl production is minimal.', '', '50', '55', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('8', 'American Lager', '73', '77', 'Medium-High', 'Medium', 'Lager', 'Wyeast', '2035', 'Liquid', '', 'Bold, complex and aromatic, good depth of flavor characteristics for a variety of lager beers.', '', '48', '58', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('9', 'American Megabrewery', '74', '76', 'Medium', 'Medium', 'Lager', 'Brewtek', 'CL-0620', 'Liquid', '', 'A smooth yeast with a slightly fruity character when fresh which lagers into a smooth clean tasting beer. Use for your lightest, cleanest Lagers or those in which you want an unobtrusive yeast character.', '', '48', '58', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('10', 'American Microbrewery Ale #1', '73', '76', 'Medium', 'Medium', 'Ale', 'Brewtek', 'CL-0010', 'Liquid', '', 'A smooth, clean, strong fermenting ale yeast that will work all the way down to 56° F. The neutral character of this yeast makes it ideal for Cream Ales and other beers in which you want maintain a clean malt flavor.', '', '56', '72', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('11', 'American Microbrewery Ale #2', '73', '75', 'Medium', 'Medium', 'Ale', 'Brewtek', 'CL-0020', 'Liquid', '', 'Gives an accentuated, rich and creamy malt profile with detectable amounts of diacetyl. Use it for lower gravity beers where the malt character should not be missed or in Strong Ales for a robust character.', '', '56', '72', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('12', 'American Microbrewery Lager', '74', '76', 'Medium', 'Medium', 'Lager', 'Brewtek', 'CL-0630', 'Liquid', '', 'This yeast leaves a clean, full flavored, malty finish despite its strong attenuation. This is an old strain, commonly used by pre-prohibition US breweries. Use CL-630 in all Lager styles you wish to have a clean, full flavor.', '', '48', '58', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('13', 'American Wheat', '74', '78', 'Medium-High', 'Low', 'Ale', 'Wyeast', '1010', 'Liquid', '', 'A dry fermenting, true top cropping yeast which produces a dry, slightly tart, crisp beer, in American hefeweisen style.', '', '58', '74', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('14', 'American White Ale', '74', '76', 'Medium', 'Low', 'Wheat', 'Brewtek', 'CL-0980', 'Liquid', '', 'A smooth, American style wheat beer yeast with an exceptionally round, clean malt flavor. The low flocculation of this yeast leaves a cloudy \"Hefe-Weizen\" yet it\'s smooth flavor makes it an integral part of a true unfiltered wheat beer.', '', '55', '68', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('15', 'Australian Ale', '74', '76', 'Medium', 'Medium', 'Ale', 'Brewtek', 'CL-0270', 'Liquid', '', 'Produces a malty, bready, nutty character with a pleasant honey like finish. This yeast emphasizes malt nuances and is very forgiving in warmer fermentations for those who cannot ferment under controlled conditions.', '', '66', '76', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('16', 'Australian Ale', '70', '75', 'Medium', 'Medium', 'Ale', 'Yeast Labs', 'A01', '', '', 'This all purpose strain produces a very complex, woody and flavorful beer.  Australian origin.  Medium attenuation.  Great for Brown ales and Porters.  65-68F.', '', '60', '75', 'Fahrenheit', '2011-12-22 15:51:14');
INSERT INTO `yeast` VALUES ('17', 'Barleywine Ale', '70', '75', 'Medium', '', 'Ale', 'Yeast Cult', 'A08', '', '', 'Tends to leave a high residual sweetness. For that Thomas Hardy clone, nothing beats the Real Thing. Styles: Barleywine, Old Ale. Origin: Dorchester, England', '', '60', '75', 'Fahrenheit', '2011-12-22 15:51:14');
INSERT INTO `yeast` VALUES ('18', 'Bavarian Lager', '73', '77', 'Medium-High', 'Medium', 'Lager', 'Wyeast', '2206', 'Liquid', '', 'Used by many German breweries to produce rich, full-bodied, malty beers. Good choice for Bocks and Dopplebocks.', '', '46', '58', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('19', 'Bavarian Lager', '70', '75', 'Medium', 'Medium', 'Lager', 'Yeast Labs', 'L32', '', '', 'Use this classic strain for medium bodied lagers and bocks, as well as Vienna and Marzen styles, rich in flavor with a clean, malty sweetness.   50-52F.', '', '45', '55', 'Fahrenheit', '2011-12-22 15:51:14');
INSERT INTO `yeast` VALUES ('20', 'Bavarian Weizen', '75', '80', 'High', 'Medium', 'Wheat', 'Yeast Cult', 'A50', '', '', 'Clove and banana esters blend well with the sweet fruitiness of wheat malt to produce a classic weizen. High temps=banana, low temps=clove. Styles: Weizen, weizenbock. Origin: Bavaria', '', '64', '66', 'Fahrenheit', '2011-12-22 15:51:14');
INSERT INTO `yeast` VALUES ('21', 'Bavarian Weizen', '70', '75', 'Medium', 'Medium', 'Wheat', 'Yeast Labs', 'W51', '', '', 'This strain produces a classic German style wheat beer, with moderately high, spicy phenolic overtones reminiscent of cloves.  66-70F.  Evidently much more consistent than Wyeast at producing a true Weizen flavor.', '', '60', '75', 'Fahrenheit', '2011-12-22 15:51:14');
INSERT INTO `yeast` VALUES ('22', 'Bavarian Wheat', '70', '76', 'Medium-High', 'Low', 'Wheat', 'Wyeast', '3638', 'Liquid', '', 'Top cropping hefeweisen yeast with complex flavor and aroma. Balance of banana and bubble gum esters with lichi and apple/plum esters and cloveness.', '', '64', '75', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('23', 'Bavarian Wheat Blend', '73', '77', 'Medium-High', 'Medium', 'Wheat', 'Wyeast', '3056', 'Liquid', '', 'Blend of top-fermenting ale and wheat strains producing mildly estery and phenolic wheat beers.', '', '64', '74', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('24', 'Belgian Abbey II', '73', '77', 'Medium-High', 'Medium', 'Ale', 'Wyeast', '1762', 'Liquid', '', 'High gravity yeast with distinct warming character from ethanol production. Slightly fruity with dry finish.', '', '65', '75', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('25', 'Belgian Ale', '78', '85', 'Medium-High', 'Medium', 'Ale', 'White Labs', 'WLP550', 'Liquid', '', 'Saisons, Belgian Ales, Belgian Reds, Belgian Browns, and White beers are just a few of the classic Belgian beer styles that can be created with this yeast strain.  Phenolic and spicy flavors dominate the profile, with less fruitiness than WLP500.', '', '68', '78', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('26', 'Belgian Ale', '72', '76', 'Medium-High', 'Medium', 'Ale', 'Wyeast', '1214', 'Liquid', '', 'Abbey-style top-fermenting yeast, suitable for high-gravity beers. Estery.', '', '58', '68', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('27', 'Belgian Ale', '75', '80', 'High', 'Medium', 'Ale', 'Yeast Cult', 'A36', '', '', 'Distinct yeast signature. Estery and fruity with a spicy nose. Styles: Belgian ales, Dubbel, Trippel. Origin: Houffalize, Belgium', '', '60', '75', 'Fahrenheit', '2011-12-22 15:51:14');
INSERT INTO `yeast` VALUES ('28', 'Belgian Ale #1', '75', '77', 'Medium', 'Medium', 'Ale', 'Brewtek', 'CL-0300', 'Liquid', '', 'For a truly classic Belgian Ale flavor. Robust and estery with big notes of clove and fruit in the araoma and flavor. Recommended for many Belgian ales, this yeast also attenuates high gravity worts well.', '', '62', '72', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('29', 'Belgian Ale #2', '74', '76', 'Medium', 'Low', 'Ale', 'Brewtek', 'CL-0320', 'Liquid', '', 'A traditional trappist strain that is particularly good in doubles and triples. This strong fermenting yeast attenuates well and produces the complex, dry, fruity, ester profile sought after in fine imported Belgian ales.', '', '62', '72', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('30', 'Belgian Ale #3', '74', '76', 'Medium', 'Medium', 'Ale', 'Brewtek', 'CL-0340', 'Liquid', '', 'Produces a classic Trappist character, with esters of spice and fruit but in a more refined way than CL-300. Mildly phenolic, this is a strong fermenting yeast, well suited to Trappist and other Belgian ales.', '', '62', '72', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('31', 'Belgian Ardennes', '72', '76', 'Medium-High', 'High', 'Wheat', 'Wyeast', '3522', 'Liquid', '', 'One of many great beer yeasts to produce classic Belgian ales. Phenolics develop with increased fermentation temperatures, mild fuitiness and complex spicy character.', '', '65', '85', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('32', 'Belgian Lambic blend', '68', '72', 'Low-Medium', 'Low', 'Lambic', 'Wyeast', '3278', 'Liquid', '', 'Contains a selection of Saccharomyces cerevisiae which include Blegian wheat beer yeast, sherry yeast, two Brettanomyces strains and Lactic Acid Bacteria. While this mixture does not include all possible cultures found in Belgian Lambics, it is representative of the organisms which are most important for the desirable flavor components of these styles.', '', '63', '75', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('33', 'Belgian Strong Ale', '74', '78', 'Medium-High', 'Low', 'Ale', 'Wyeast', '1388', 'Liquid', '', 'Robust flavor yeast with moderate to high alcohol tolerance. Fruity nose and palate, dry, tart finish.', '', '65', '75', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('34', 'Belgian Wheat', '72', '74', 'Medium', 'Medium', 'Wheat', 'Brewtek', 'CL-0900', 'Liquid', '', 'A top fermenting yeast which produces a soft, bread like flavor and leaves a pleasant, mildly estery finish. Combine with Curacao and coriander to make a delicious, Belgian Witbier or French Biere Blanche.', '', '55', '68', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('35', 'Belgian Wheat', '72', '76', 'Medium-High', 'Medium', 'Wheat', 'Wyeast', '3942', 'Liquid', '', 'Estery low phenol producing yeast from small Belgian brewery. Apple and plum like nose with dry finish.', '', '64', '74', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('36', 'Belgian Wheat', '70', '75', 'Medium', '', 'Wheat', 'Yeast Labs', 'W52', '', '', 'Yeast used in the production of Belgian White beer (Wit).  This strain provides a soft elegant finish with moderate esters and mild, spicy phenols.  66-70F.', '', '60', '75', 'Fahrenheit', '2011-12-22 15:51:14');
INSERT INTO `yeast` VALUES ('37', 'Belgian Wit', '65', '71', 'Low-Medium', 'Medium', 'Wheat', 'Yeast Cult', 'A35', '', '', 'Spicy, slight phenolic character compliments orange and coriander. Origin: Central Belgium', '', '60', '75', 'Fahrenheit', '2011-12-22 15:51:14');
INSERT INTO `yeast` VALUES ('38', 'Belgian Wit Ale', '74', '78', 'Medium-High', 'Low', 'Wheat', 'White Labs', 'WLP400', 'Liquid', '', 'Slightly phenolic and tart, this is the yeast used to produce Wit in Belgium.', '', '67', '74', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('39', 'Belgian Witbier', '72', '76', 'Medium-High', 'Medium', 'Wheat', 'Wyeast', '3944', 'Liquid', '', 'A tart, slightly phenolic character capable of producing distinctive witbiers and grand cru-style ales alike. Alcohol tolerant.', '', '62', '75', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('40', 'Bohemian Lager', '69', '73', 'Low-Medium', 'Medium', 'Lager', 'Wyeast', '2124', 'Liquid', '', 'A pilsner yeast from the Weihenstephen. Ferments clean and malty, with rich residual maltiness in full gravity pilsners.', '', '48', '58', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('41', 'Brettanomyces Bruxellensis', '65', '69', 'Low', 'Medium', 'Lambic', 'Wyeast', '3112', 'Liquid', '', 'Wild yeast isolated from brewerys in the Brussels region. Produces the classic sweaty horse hair character indigenous to this region; gueuze, lambics, sour browns. Ferments best in worts with lower pH after primary ferment has begun. This strain is generally used in conjunction with S. Cerevisiae as well as wild yeast and lactic bacteria.', '', '60', '75', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('42', 'Brett/Pedio Blend', '70', '75', 'Medium', '', 'Lambic', 'Brewtek', 'CL-5000', '', '', 'Blend of Brettanomyces Lambicus and Pediococcus Damnosus', '', '60', '75', 'Fahrenheit', '2011-12-22 15:51:14');
INSERT INTO `yeast` VALUES ('43', 'Brettanomyces lambicus', '71', '75', 'Medium', 'Medium', 'Lambic', 'Brewtek', 'CL-5200', 'Liquid', '', 'Wild yeast strain associated with the country-side breweries of Belgian.  This yeast is an important contributor to the flavor profile of lambic beers and contributes a unique and complex flavor sometimes described as \"horsey\" or \"old leather.\" A slow-growing yeast which takes several weeks to ferment and develop its unique characte', '', '48', '68', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('44', 'Brettanomyces lambicus', '65', '70', 'Low', 'Medium', 'Lambic', 'Wyeast', '3526', '', '', 'Wild yeast isolated from Belgian lambic beers. Produces a pie cherry like flavor and sourness along with distinct brett character. Ferments best in worts with reduced pH after primary fermentation has begun, and may form a pellicle. Works best in conjunction with other yeast and lactic bacteria to produce the classic Belgian character.', '', '60', '75', 'Fahrenheit', '2011-12-22 15:51:14');
INSERT INTO `yeast` VALUES ('45', 'British Ale', '67', '74', 'Low-Medium', 'High', 'Ale', 'White Labs', 'WLP005', 'Liquid', '', 'This yeast is a little more attenuative than WLP002. Like most English strains, this yeast produces malty beers. Excellent for all English style ales including bitter, pale ale, porter, and brown ale.', '', '67', '74', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('46', 'British Ale', '73', '75', 'Medium', 'Medium', 'Ale', 'Wyeast', '1098', 'Liquid', '', 'From Whitbread. Ferments dry and crisp, slightly tart, fruity and well-balanced. Ferments well down to 65oF.', '', '64', '72', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('47', 'British Ale', '75', '70', 'Medium', 'Medium', 'Ale', 'Yeast Labs', 'A04', '', '', 'This strain produces a great light bodied ale, excellent for Pale Ales and Brown Ales, with a complex estery flavor.  Ferments dry with a sharp finish.  65-68F.', '', '60', '75', 'Fahrenheit', '2011-12-22 15:51:14');
INSERT INTO `yeast` VALUES ('48', 'British Ale II', '73', '76', 'Medium-High', 'High', 'Ale', 'Wyeast', '1335', 'Liquid', '', 'Typical of British and Canadian ale fermentation profile with good flocculating and malty flavor characteristics, crisp finish, clean, fairly dry.', '', '63', '75', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('49', 'British Draft Ale', '72', '78', 'Medium', 'Medium', 'Ale', 'Brewtek', 'CL-0160', 'Liquid', '', 'Gives a full bodied, well rounded flavor with threshold diacetyl. This yeast has a way of emphasizing malt character like no other yeast we\'ve used. Highly recommended for many ale styles. Oxygenation will greatly affect attenuation.', '', '62', '72', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('50', 'British Microbrewery Ale', '73', '75', 'Medium', 'Medium', 'Ale', 'Brewtek', 'CL-0110', 'Liquid', '', 'Provides a complex, oakey, fruity ester profile and full flavored finish, suitable to low and medium gravity British ale styles. Very distinct, this classic, old fashioned yeast is a great for traditional bitters and is a rare find for mild ale fans.', '', '62', '72', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('51', 'British Pale Ale #1', '75', '77', 'Medium', 'Medium', 'Ale', 'Brewtek', 'CL-0120', 'Liquid', '', 'This yeast produces a bold, estery character and accentuates mineral and hop flavors. The distinct flavor of this yeast makes it well suited for use in your best Burton Pale Ale or Bitter recipes.', '', '62', '72', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('52', 'British Pale Ale #2', '74', '76', 'Medium', 'Medium', 'Ale', 'Brewtek', 'CL-0130', 'Liquid', '', 'A smooth, full flavored ale yeast. Mildly estery, this yeast is smooth, well rounded and accentuates caramel and other malt nuances. It is a strong fermenter and is also good for strong or spiced ales.', '', '62', '72', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('53', 'British Real Ale', '66', '70', 'Medium', 'Medium', 'Ale', 'Brewtek', 'CL-0150', 'Liquid', '', 'For those longing for the character of a real pub bitter. This yeast has a complex, woody, and fruity ester profile that charactarizes many real ales. Typically under attenuating, the malt profile is left intact with a mild sweetness in the finish.', '', '62', '72', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('54', 'Burton Ale', '69', '75', 'Low-Medium', 'Medium', 'Ale', 'White Labs', 'WLP023', 'Liquid', '', 'From the famous brewing town of Burton upon Trent, England, this yeast is packed with character. It provides delicious subtle fruity flavors like apple, clover honey and pear. Great for all English styles, IPA\'s bitters, pales. Excellent in porters and stouts.', '', '68', '73', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('55', 'California Ale', '73', '80', 'Medium-High', 'High', 'Ale', 'White Labs', 'WLP001', 'Liquid', '', 'This yeast is famous for its clean flavors, balance and ability to be used in almost any style ale. It accentuates the hop flavors and is extremely versatile.', '', '68', '73', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('56', 'California Gold', '74', '76', 'Medium', 'Medium', 'Lager', 'Brewtek', 'CL-0690', 'Liquid', '', 'Use to recreate \"California common beers\". Leaves a slightly estery, well attenuated finish. The character of this yeast is quite distinct, try it in American or robust Porters for a unique flavor profile.', '', '48', '65', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('57', 'California Lager', '67', '71', 'Low-Medium', 'High', 'Lager', 'Wyeast', '2112', 'Liquid', '', 'Particularly suited for producing 19th century-style West Coast beers. Retains lager characteristics at temperatures up to 65o F, and produces malty, brilliantly-clear beers.', '', '58', '68', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('58', 'California Lager', '75', '70', 'Medium', 'High', 'Lager', 'Yeast Labs', 'L35', '', '', 'A California common beer strain, malty with a sweet, woody flavor and subtle fruitiness.  64-66F.', '', '45', '55', 'Fahrenheit', '2011-12-22 15:51:14');
INSERT INTO `yeast` VALUES ('59', 'California Pub Brewery', '74', '76', 'Medium', 'Medium', 'Ale', 'Brewtek', 'CL-0050', 'Liquid', '', 'For that classic U.S. small brewery flavor. CL-50 produces terrific American red & pale ale styles. While attenuation is normal, this yeast produces a big, soft, well rounded malt flavor that really lets caramel malt flavors shine.', '', '60', '70', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('60', 'Canadian Ale', '73', '77', 'Medium', 'Medium', 'Ale', 'Brewtek', 'CL-0260', 'Liquid', '', 'Produces a malty, bready, nutty character with a pleasant honey like finish. This yeast emphasizes malt nuances and is very forgiving in warmer fermentation conditions.', '', '62', '72', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('61', 'Canadian Ale', '75', '80', 'High', 'High', 'Ale', 'Yeast Labs', 'A07', '', '', 'This strain produces a light bodied, clean and flavorful beer, very fruity when fermented cool.  Good for light and cream ales.  65-66F.', '', '60', '75', 'Fahrenheit', '2011-12-22 15:51:14');
INSERT INTO `yeast` VALUES ('62', 'Champagne', '75', '80', 'Medium', 'Low', 'Champagne', 'White Labs', 'WLP715', 'Liquid', '', 'Classic yeast, used to produce champagne, cider, dry meads, dry wines, or to fully attenuate barley wines/ strong ales. Can tolerate alcohol concentrations up to 17%', '', '70', '75', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('63', 'Champagne', '75', '80', 'High', 'Medium', 'Champagne', 'Yeast Cult', 'W06', '', '', 'A vigorous and thorough fermenter, very alcohol tolerant. Helpful in stuck fermentations or ultra-high gravity brewing. Styles: Wine, Mead, Barleywines. Origin: Montreal, Canada', '', '60', '75', 'Fahrenheit', '2011-12-22 15:51:14');
INSERT INTO `yeast` VALUES ('64', 'Classic British Ale', '75', '77', 'Medium', 'Medium', 'Ale', 'Brewtek', 'CL-0170', 'Liquid', '', 'Produces a beautiful draft bitter or Porter. This yeast leaves a complex ale with very British tones and fruit like esters, yet it also attenuates well, leaving a dry finish. It is a good choice for high gravity worts.', '', '62', '72', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('65', 'Czech Pils', '70', '74', 'Medium', 'Medium', 'Lager', 'Wyeast', '2278', 'Liquid', '', 'Classic pilsner strain from the home of pilsners for a dry, but malty finish. The perfect choice for pilsners and bock beers. Sulfur produced during fermentation dissipates with conditioning.', '', '50', '58', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('66', 'Danish Lager', '73', '77', 'Medium-High', 'Medium', 'Lager', 'Wyeast', '2042', 'Liquid', '', 'Rich, dortmund-style, crisp, dry finish. Soft profile accentuates hop characteristics.', '', '46', '56', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('67', 'Dry Mead', '75', '70', 'Medium', '', 'Mead', 'Yeast Labs', 'M61', '', '', 'Very alcohol tolerant, ferments dry, fruity and clean, yet leaves a noticeable honey flavor and aroma.  65-70F.', '', '60', '75', 'Fahrenheit', '2011-12-22 15:51:14');
INSERT INTO `yeast` VALUES ('68', 'Dusseldorf Ale', '75', '70', 'Medium', 'High', 'Ale', 'Yeast Labs', 'A06', '', '', 'German Altbier yeast strain finishes with full body, complex flavor and spicy sweetness.  65-68F.', '', '60', '75', 'Fahrenheit', '2011-12-22 15:51:14');
INSERT INTO `yeast` VALUES ('69', 'East Coast Ale', '70', '75', 'Medium', 'Low', 'Ale', 'White Labs', 'WLP008', 'Liquid', '', 'Our \"Brewer Patriot\" strain can be used to reproduce many of the American versions of classic beer styles. Similar neutral character of WLP001, but less attenuation, less accentuation of hop bitterness, increased flocculation, and a little tartness. Very clean and low esters. Great yeast for golden, blonde, honey, pales and German alt style ales.', '', '68', '73', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('70', 'East European Lager', '75', '78', 'Medium', 'Medium', 'Lager', 'Brewtek', 'CL-0680', 'Liquid', '', 'From a very old, European brewery, CL-680 imparts a smooth, rich, almost creamy character, emphasizing a big malt flavor and clean finish. This is our choice when brewing lagers in which the malt character should be full and smooth.', '', '48', '58', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('71', 'Edinburgh Ale', '70', '75', 'Medium', 'Medium', 'Ale', 'White Labs', 'WLP028', 'Liquid', '', 'Scotland is famous for its malty, strong ales. This yeast can reproduce complex, flavorful Scottish style ales. Does not ferment well under 62°F.', '', '65', '70', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('72', 'English Ale', '63', '70', 'Low', 'Very High', 'Ale', 'White Labs', 'WLP002', 'Liquid', '', 'A classic ESB strain from one of England\'s largest breweries. This yeast is best suited for English style ales including milds, bitters, porters, and English style stouts. This leaves behind some residual sweetness.', '', '65', '68', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('73', 'English Ale', '75', '70', 'Medium', 'Medium', 'Ale', 'Yeast Cult', 'A15', '', '', 'Complex, with strong yeast flavors. Used in many English-style brewpubs. Styles: Brown ale, English Bitter, Milds, IPA, Pale Ale. Origin: Ireland', '', '60', '75', 'Fahrenheit', '2011-12-22 15:51:14');
INSERT INTO `yeast` VALUES ('74', 'English Ale', '75', '70', 'Medium', '', 'Ale', 'Yeast Labs', 'A09', '', '', 'A old English brewery strain, this clean yeast is fairly neutral in character, producing a fruity, soft and estery finish.  A vigorous fermenter.  64-66F.', '', '60', '75', 'Fahrenheit', '2011-12-22 15:51:14');
INSERT INTO `yeast` VALUES ('75', 'European Ale', '67', '71', 'Low-Medium', 'High', 'Ale', 'Wyeast', '1338', 'Liquid', '', 'From Wissenschaftliche in Munich. Full-bodied complex strain finishing very malty. Produces a dense, rocky head during fermentation.', '', '62', '72', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('76', 'European Lager II', '73', '77', 'Medium-High', 'Low', 'Lager', 'Wyeast', '2247', 'Liquid', '', 'Clean dry flavor profile often used in aggressively hopped pilsner. Mild aromatics, slight sulpher production, dry finish.', '', '46', '56', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('77', 'Forbidden Fruit', '72', '76', 'Medium-High', 'Low', 'Wheat', 'Wyeast', '3463', 'Liquid', '', 'From classic Belgian brewery for production of wits to classic grand cru. Phenolic profile with subdued fruitiness. Seasonal availability.', '', '63', '76', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('78', 'German Ale', '73', '77', 'Medium-High', 'Low', 'Ale', 'Wyeast', '1007', 'Liquid', '', 'Ferments dry and crisp, leaving a complex but mild flavor. Produces an extremely rocky head and ferments well down to 55o F.', '', '55', '66', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('79', 'German Ale', '75', '70', 'Medium', 'High', 'Ale', 'Yeast Cult', 'A04', '', '', 'Clean and fruity, produces exquisitely flavorful light-bodied ales. Good top cropping ale strain. A true Cologne strain with mild yeast character. Styles: Kolsch, Cream ale. Origin: Cologne, Germany', '', '60', '75', 'Fahrenheit', '2011-12-22 15:51:14');
INSERT INTO `yeast` VALUES ('80', 'German Ale/Kolsch', '72', '78', 'Medium-High', 'Medium', 'Ale', 'White Labs', 'WLP029', 'Liquid', '', 'From a small brewpub in Cologne, Germany, this yeast works great in Kölsch and Alt style beers. Good for light beers like blond and honey. The slight sulfur produced during fermentation will disappear with age and leave a super clean, lager like ale. Does not ferment well under 62°F', '', '65', '69', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('81', 'German Lager', '74', '79', 'Medium-High', 'Medium', 'Lager', 'White Labs', 'WLP830', 'Liquid', '', 'This yeast is one of the most widely used lager yeasts in the world. Very malty and clean, great for all German lagers, pilsner, oktoberfest, and marzen.', '', '50', '55', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('82', 'German Lager', '75', '80', 'High', 'Medium', 'Lager', 'Yeast Cult', 'L09', '', '', 'Common strain in use in many German lager breweries. Malty accent. Styles: German lagers & bocks, most lager styles. Origin: Bavaria', '', '48', '52', 'Fahrenheit', '2011-12-22 15:51:14');
INSERT INTO `yeast` VALUES ('83', 'German Weiss', '74', '76', 'Medium', 'Medium', 'Wheat', 'Brewtek', 'CL-0930', 'Liquid', '', 'Milder than CL-920, our 930 strain, from a famous German yeast bank, still produces the sought after clove and phenol characters but to a lesser degree, with a fuller, earthier character.', '', '55', '68', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('84', 'German Wheat', '75', '77', 'High', 'Medium', 'Wheat', 'Brewtek', 'CL-0920', 'Liquid', '', 'A true, top fermenting Weizenbier yeast. Intensely spicy, clovey and phenolic. This yeast is highly attenuative and flocks in large, loose clumps. Use for All Weizen recipes and is particularly good in Wiezenbocks.', '', '55', '68', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('85', 'German Wheat', '70', '76', 'Medium-High', 'High', 'Wheat', 'Wyeast', '3333', 'Liquid', '', 'Subtle flavor profile for wheat yeast with sharp tart crispness, fruity, sherry-like palate.', '', '63', '75', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('86', 'Hefeweizen Ale', '72', '76', 'Medium-High', 'Low', 'Wheat', 'White Labs', 'WLP300', 'Liquid', '', 'This famous German yeast is a strain used in the production of traditional, authentic wheat beers. It produces the banana and clove nose traditionally associated with German wheat beers and leaves the desired cloudy look of traditional German wheat beers.', '', '68', '72', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('87', 'Hefeweizen IV Ale', '73', '80', 'Medium-High', 'Low', 'Wheat', 'White Labs', 'WLP380', 'Liquid', '', 'Large clove and phenolic aroma and flavor, with minimal banana. Refreshing citrus and apricot notes. Crisp, drinkable hefeweizen. Less flocculant than WLP300, and sulfur production is higher.', '', '66', '70', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('88', 'Irish Ale', '69', '74', 'Low-Medium', 'Medium', 'Ale', 'White Labs', 'WLP004', 'Liquid', '', 'This is the yeast from one of the oldest stout producing breweries in the world. It produces a slight hint of diacetyl, balanced by a light fruitiness and slight dry crispness. Great for Irish ales, stouts, porters, browns, reds and a very interesting pale ale.', '', '65', '68', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('89', 'Irish Ale', '71', '75', 'Medium', 'Medium', 'Ale', 'Wyeast', '1084', 'Liquid', '', 'Slight residual diacetyl and fruitiness; great for stouts. Clean, smooth, soft and full-bodied.', '', '62', '72', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('90', 'Irish Ale', '75', '80', 'High', 'Medium', 'Ale', 'Yeast Cult', 'A13', '', '', 'The Real Thing from Ireland. Nutty, woody and complex. Styles: Dry Stout, Imperial Stout. Origin: Dublin, Ireland', '', '60', '75', 'Fahrenheit', '2011-12-22 15:51:14');
INSERT INTO `yeast` VALUES ('91', 'Irish Ale', '75', '80', 'High', 'High', 'Ale', 'Yeast Labs', 'A05', '', '', 'This top fermenting strain is ideal for Stouts and Porters.  Slightly acidic, with a hint of butterscotch in the finish, soft and full bodied.  65-68F.', '', '60', '75', 'Fahrenheit', '2011-12-22 15:51:14');
INSERT INTO `yeast` VALUES ('92', 'Irish Dry Stout', '76', '78', 'High', 'Medium', 'Ale', 'Brewtek', 'CL-0240', 'Liquid', '', 'A true, old fashioned, top fermenting yeast which leaves a very recognizable character to Dry Stouts. Has a vinous, almost lactic character which blends exceptionally well with roasted malts. Highly attenuative.', '', '62', '72', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('93', 'Kolsch', '73', '77', 'Medium-High', 'Low', 'Ale', 'Wyeast', '2565', 'Liquid', '', 'A hybrid of ale and lager characteristics. This strain develops excellent maltiness with subdued fruitiness, and a crisp finish. Ferments well at moderate temperatures.', '', '56', '64', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('94', 'Kolsch', '75', '78', 'Medium', 'Medium', 'Ale', 'Brewtek', 'CL-0450', 'Liquid', '', 'Produces an astonishing clean lager like flavor at ale temperatures. Matures with time into a clean, well attenuated flavor. Mineral and malt characters come through well, with a clean, lightly yeasty flavor and aroma in the finish.', '', '62', '72', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('95', 'Lactobacillus delbrueckii', '65', '69', 'Medium', 'Medium', 'Lambic', 'Wyeast', '4335', 'Liquid', '', 'Lactic acid bacteria isolated from a Belgian Brewery. This culture produces moderated levels of acidity and is commonly found in many types of beers including gueuze, lambics, sour brown ales and Berliner Weisse. Always used in conjunction with S. cerevisiae and often with various wild yeast.', '', '60', '95', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('96', 'London Ale', '73', '77', 'Medium-High', 'Medium', 'Ale', 'Wyeast', '1028', 'Liquid', '', 'Rich with a dry finish, minerally profile, bold and crisp, with some fruitiness.', '', '60', '72', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('97', 'London Ale', '75', '70', 'Medium', 'Medium', 'Ale', 'Yeast Labs', 'A03', '', '', 'Classic Pale Ale strain, very dry.  A powdery yeast with a hint of diacetyl and a rich minerally profile, crisp and clean.  65-68F.', '', '60', '75', 'Fahrenheit', '2011-12-22 15:51:14');
INSERT INTO `yeast` VALUES ('98', 'London Ale III', '71', '75', 'Medium', 'High', 'Ale', 'Wyeast', '1318', 'Liquid', '', 'From traditional London brewery with great malt and hop profile. True top cropping strain, fruity, very light, soft balanced palate, finishes slightly sweet.', '', '64', '74', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('99', 'London ESB Ale', '67', '71', 'Low-Medium', 'High', 'Ale', 'Wyeast', '1968', 'Liquid', '', 'Highly flocculant top-fermenting strain with rich, malty character and balanced fruitiness. This strain is so flocculant that additional aeration and agitation is needed. An excellent strain for cask-conditioned ales.', '', '64', '72', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('100', 'Munich Lager', '73', '77', 'Medium-High', 'Medium', 'Lager', 'Wyeast', '2308', 'Liquid', '', 'A unique strain, capable of producing fine lagers. Very smooth, well-rounded and full-bodied. Benefits from temperature rise for diacetyl rest.', '', '48', '56', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('101', 'Munich Lager', '75', '70', 'Medium', 'Medium', 'Lager', 'Yeast Labs', 'L33', '', '', 'German brewing strain for medium bodied lagers and bocks, subtle and complex flavors, smooth and soft, a hint of sulfur when fresh.  48-50F.', '', '45', '55', 'Fahrenheit', '2011-12-22 15:51:14');
INSERT INTO `yeast` VALUES ('102', 'North American Lager', '70', '76', 'Medium-High', 'High', 'Lager', 'Wyeast', '2272', 'Liquid', '', 'Traditional culture of North American and Canadian lager and light pilsners. Malty finish.', '', '48', '56', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('103', 'North German Lager', '74', '76', 'Medium', 'Medium', 'Lager', 'Brewtek', 'CL-0660', 'Liquid', '', 'Exhibits the classic clean, crisp, traditional Northern German Lager character. Used in German Pilsners, Mexican and Canadian Lagers, this strong Fermenting, forgiving Lager yeast is an excellent strain for many Lagers.', '', '48', '58', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('104', 'Northwest Ale', '67', '71', 'Low-Medium', 'High', 'Ale', 'Wyeast', '1332', 'Liquid', '', 'One of the classic ale strains from the Northwest U.S. Breweries. Produces a malty and mildly fruity ale with good depth and complexity.', '', '65', '75', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('105', 'Octoberfest/Marzen Lager', '65', '73', 'Low-Medium', 'Medium', 'Lager', 'White Labs', 'WLP820', 'Liquid', '', 'This yeast produces a very malty, bock like style. It does not finish as dry as WLP830. This yeast is much slower in the first generation than WLP830, so we encourage a larger starter to be used the first generation or schedule a longer lagering time.', '', '52', '58', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('106', 'Old Bavarian Lager', '74', '76', 'Medium', 'Medium', 'Lager', 'Brewtek', 'CL-0650', 'Liquid', '', 'Well rounded and malty with a subtle ester complex and complex undertones. This distinct, Southern German yeast strain is great for full flavored, classic German lagers such as Bock, Dunkel and Helles styles.', '', '48', '58', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('107', 'Old German Ale', '75', '77', 'Medium', 'Medium', 'Ale', 'Brewtek', 'CL-0400', 'Liquid', '', 'For traditional Alt Biers, a strong fermenter which leaves a smooth, attenuated, yet mild flavor. Use in your favorite German Ale recipes. Also makes a slightly dry but clean, quenching wheat beer.', '', '62', '72', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('108', 'Original Pilsner', '75', '76', 'Medium', 'Medium', 'Lager', 'Brewtek', 'CL-0600', 'Liquid', '', 'Leaves a full bodied Lager with a mildly under attenuated finish and subdued diacetyl character. Use this distinct yeast in classic Czechoslovakian Pilsners or any lager in which you would like to emphasize a big, malty palate.', '', '48', '58', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('109', 'Pale Ale', '75', '70', 'Medium', 'Medium', 'Ale', 'Yeast Cult', 'A17', '', '', 'Very smooth and mellow with a distinct and complex yeast signature. Styles: Brown ale, English Bitter, Milds, IPA, Pale ale. Origin: London, England', '', '60', '75', 'Fahrenheit', '2011-12-22 15:51:14');
INSERT INTO `yeast` VALUES ('110', 'Pediococcus cerevisiae', '65', '69', 'Medium', 'Medium', 'Lambic', 'Wyeast', '4733', 'Liquid', '', 'Lactic acid bacteria used in the production of Belgian style beers where additional acidity is desirable. Often found in gueuze and other Belgian style beer. High acid producer which usually increases overall acid levels in beer as storage time increases.', '', '60', '95', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('111', 'Pediococcus damnosus', '71', '75', 'Medium', 'Medium', 'Lambic', 'Brewtek', 'CL-5600', 'Liquid', '', 'Lactic acid producing bacteria found in lambic beers.  This is is a slow-growing bacteria which prefers anaerobic (no oxygen) conditions.  It is also common brewery contaminant which produces large amounts of diacetyl.', '', '48', '68', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('112', 'Pilsen Lager', '71', '75', 'Medium', 'Medium', 'Lager', 'Wyeast', '2007', 'Liquid', '', 'A classic American pilsner strain, smooth, malty palate. Ferments dry and crisp.', '', '48', '56', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('113', 'Pilsner Lager', '72', '77', 'Medium-High', 'High', 'Lager', 'White Labs', 'WLP800', 'Liquid', '', 'Classic pilsner strain from the premier pilsner producer in the Czech Republic. Somewhat dry with a malty finish, this yeast is best suited for European pilsner production.', '', '50', '55', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('114', 'Pilsner Lager', '75', '80', 'High', 'Medium', 'Lager', 'Yeast Labs', 'L31', '', '', 'This classic strain produces a light lager in both flavor and body, fermenting dry and clean.  50-52F.', '', '45', '55', 'Fahrenheit', '2011-12-22 15:51:14');
INSERT INTO `yeast` VALUES ('115', 'Pilzen Lager', '75', '70', 'Medium', 'Medium', 'Lager', 'Yeast Cult', 'L17', '', '', 'Extremely clean, malty and full bodied. Styles: Pilsner, American lager. Origin: Pilzen', '', '48', '52', 'Fahrenheit', '2011-12-22 15:51:14');
INSERT INTO `yeast` VALUES ('116', 'Ringwood Ale', '68', '72', 'Low-Medium', 'High', 'Ale', 'Wyeast', '1187', 'Liquid', '', 'Notorious Yeast of European origin with unique fermentation and flavor charateristics. Distinct fruit ester and high flocculation provide a malty complex profile, which clears well. Thorough diacetyl rest is recommended after fermentation is complete.', '', '64', '74', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('117', 'Saison', '74', '76', 'Medium', 'Medium', 'Ale', 'Brewtek', 'CL-0380', 'Liquid', '', 'This yeast leaves a smooth, full character in the malt, with mild yet pleasant esters and flavors reminiscent of apple pie spices. Best used to recreate country French and Belgian Ales as well as Grand Cru styles.', '', '62', '72', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('118', 'San Francisco Lager', '65', '70', 'Low', 'High', 'Lager', 'White Labs', 'WLP810', 'Liquid', '', 'This yeast is used to produce the \"California Common\" style beer. A unique lager strain which has the ability to ferment up to 65 degrees while retaining lager characteristics. Can also be fermented down to 50 degrees for production of marzens, pilsners and other style lagers.', '', '58', '65', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('119', 'Scotch Ale', '75', '80', 'High', 'Medium', 'Ale', 'Yeast Cult', 'A34', '', '', 'Clean, ferments well at cool temperatures leaving a rich malty profile. Styles: Scotch ale, Scottish bitters, Strong ale. Origin: Edinburgh, Scotland', '', '55', '65', 'Fahrenheit', '2011-12-22 15:51:14');
INSERT INTO `yeast` VALUES ('120', 'Scottish Ale', '77', '80', 'Medium', 'High', 'Ale', 'Brewtek', 'CL-0200', 'Liquid', '', 'Use for classic Scottish heavys, 90\'s- or strong ales. This yeast produces a soft, fruity malt profile with a subtle woody, oakey ester profile. A mild, mineral like dryness in the finish makes this a very complex strain.', '', '60', '68', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('121', 'Scottish Ale', '69', '73', 'Low-Medium', 'High', 'Ale', 'Wyeast', '1728', 'Liquid', '', 'Ideally suited for Scottish-style ales, and high gravity ales of all types.', '', '55', '75', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('122', 'Scottish Bitter', '75', '70', 'Medium', '', 'Ale', 'Brewtek', 'CL-0210', '', '', 'Has a soft, yeasty, fruity nose which yields to a well attenuated malt flavor and big ester complex of ripe fruit, apricots and rose petals. Dry and complex, yet smooth and fruity with mild sulphur.', '', '60', '75', 'Fahrenheit', '2011-12-22 15:51:14');
INSERT INTO `yeast` VALUES ('123', 'St. Louis Lager', '75', '80', 'High', 'Medium', 'Lager', 'Yeast Labs', 'L34', '', '', 'This strain produces a round, very crisp and clean fruity flavor, with medium body.  Good for American style lagers.  50-52F.', '', '45', '55', 'Fahrenheit', '2011-12-22 15:51:14');
INSERT INTO `yeast` VALUES ('124', 'Stout Ale', '65', '70', 'Low', 'Medium', 'Ale', 'Yeast Cult', 'A06', '', '', 'Slight residual diacetyl that blends well with the dry bitterness of roast barley. Styles: Brown ale, Porter, Stout, Imperial Stout. Origin: Ireland', '', '60', '75', 'Fahrenheit', '2011-12-22 15:51:14');
INSERT INTO `yeast` VALUES ('125', 'Sweet Mead', '75', '70', 'Medium', '', 'Mead', 'Yeast Labs', 'M62', '', '', 'This strain has slightly reduced alcohol tolerance and produces a very fruity, sweet mead with tremendous honey aromas.  65-70F.', '', '60', '75', 'Fahrenheit', '2011-12-22 15:51:14');
INSERT INTO `yeast` VALUES ('126', 'Sweet Mead/Wine', '70', '75', 'Medium', 'Low', 'Mead', 'White Labs', 'WLP720', 'Liquid', '', 'A wine yeast strain that is less attentuative than WLP715, leaving some residual sweetness. Slightly fruity and will tolerate alcohol concentrations up to 15%', '', '70', '75', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('127', 'Swiss Lager', '74', '76', 'Medium', 'Medium', 'Lager', 'Brewtek', 'CL-0670', 'Liquid', '', 'A unique strain that has both a clean, crisp lager flavor and a soft, smooth maltiness. Perfect for European Pilsners. Like our CL-660 strain, this is an excellent all purpose Lager yeast for those wanting a fuller, rounder palate.', '', '48', '54', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('128', 'Thames Valley Ale', '76', '78', 'Medium-High', 'Medium', 'Ale', 'Wyeast', '1275', 'Liquid', '', 'Produces classic British bitters, rich complex flavor profile, clean, light malt character, low fruitiness, low esters, well-balanced.', '', '62', '72', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('129', 'Trappe Ale', '75', '80', 'High', 'Medium', 'Ale', 'Yeast Cult', 'A16', '', '', 'Typical Trappist esters and aroma. Good selection for a Chimay clone. Styles: Trappist Ales. Origin: Belgian Monastery', '', '60', '75', 'Fahrenheit', '2011-12-22 15:51:14');
INSERT INTO `yeast` VALUES ('130', 'Trappist Ale', '75', '80', 'Medium-High', 'Medium', 'Ale', 'White Labs', 'WLP500', 'Liquid', '', 'From one of the six Trappist breweries remaining in the world, this yeast produces the distinctive fruitiness and plum characteristics. Excellent yeast for high gravity beers, Belgian ales, dubbels and trippels.', '', '65', '72', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('131', 'Trappist Ale', '75', '80', 'High', 'High', 'Ale', 'Yeast Labs', 'A08', '', '', 'This is a typical Belgian strain, producing a malty flavor with a balance of fruity, phenolic overtones when fermented warm.  Alcohol tolerant.  64-70F.', '', '60', '75', 'Fahrenheit', '2011-12-22 15:51:14');
INSERT INTO `yeast` VALUES ('132', 'Trappist High Gravity', '74', '78', 'High', 'Medium', 'Wheat', 'Wyeast', '3787', 'Liquid', '', 'Robust top cropping yeast with phenolic character. Alcohol tolerance to 12%. Ideal for Biere de Garde. Ferments dry with rich ester profile and malty palate.', '', '64', '78', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('133', 'Weihenstephan Weizen', '73', '77', 'Medium-High', 'Low', 'Wheat', 'Wyeast', '3068', 'Liquid', '', 'Unique top-fermenting yeast which produces the unique and spicy weizen character, rich with clove, vanilla and banana. Best results are achieved when fermentations are held around 68o F.', '', '64', '75', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('134', 'Whitbread Ale', '68', '72', 'Low-Medium', 'High', 'Ale', 'Wyeast', '1099', 'Liquid', '', 'A mildly malty and slightly fruity fermentation profile; not as tart and dry as 1098 and much more flocculant. Clears well without filtration.', '', '64', '75', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('135', 'Wyeast Ale blend', '71', '75', 'Medium', 'Medium', 'Ale', 'Wyeast', '1087', 'Liquid', '', 'A blend of the best ale strains to provide quick starts, good flavor and good flocculation. The profile of these strains provides a balanced finish for British and American style ales.', '', '64', '72', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('136', 'Wyeast Lager blend', '71', '75', 'Medium', 'Medium', 'Lager', 'Wyeast', '2178', 'Liquid', '', 'A blend of the Brewer\'s ChoiceTM lager strains for the most complex flavor profiles. For production of classic pilsners, to full bodied \"bock\" beers.', '', '48', '58', 'Fahrenheit', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('137', 'Abbey Ale', '75', '80', null, 'Medium', 'Ale', 'White Labs', 'WLP530', 'Liquid', '', 'Used in two of six remaining Trappist breweries.  Distinctive plum and fruitiness.  Good for high gravity beers.|Belgian Trappist Ale, Spiced Ale, Trippel, Dubbel, Grand Cru', '', '66', '72', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('138', 'Abbey IV Ale', '74', '82', null, 'Medium', 'Ale', 'White Labs', 'WLP540', 'Liquid', '', 'An authentic Trappist style ale yeast.  Use for Belgian ales including abbey ales (dubbels, tripels).  Fruit character is medium - between WLP500 (high) and WLP530 (low)|Trappist Belgian Ales, Dubbels, Tripels and Specialty ales', '', '66', '72', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('139', 'American Ale Yeast Blend', '72', '80', null, 'Medium', 'Ale', 'White Labs', 'WLP060', 'Liquid', '', 'A blend that celebrates WLP001 (California Ale Yeast\'s) clean, neutral fermentation.  This strain is versatile and adds two other yeast strains that are also clean/neutral in flavor to add a bit of complexity - almost a lager like finish.  Slight sulfur m|American ales with clean finish', '', '68', '72', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('140', 'American Farmhouse Blend', '75', '82', null, 'Medium', 'Ale', 'White Labs', 'WLP670', 'Liquid', '', 'Inspired by local American brewers crafting semi-  traditional Belgian-style ales. This blend creates a complex flavor profile with a moderate level of sourness. It consists of a traditional farmhouse yeast strain and Brettanomyces. Great yeast for farmho|Saisons, Farmhouse Ales', '', '68', '72', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('141', 'Antwerp Ale', '73', '80', null, 'Medium', 'Ale', 'White Labs', 'WLP515', 'Liquid', '', 'Clean, almost lager like Belgian ale yeast.  Good for Belgian pale and amber ales or with other Belgian yeasts in a blend.  Biscuity, ale like aroma present.  Hop flavors are accentuated.  Slight sulfur during fermentation, and a lager like flavor profile|Belgian pale and amber ales', '', '67', '70', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('142', 'Assmanshausen Wine', '80', '86', null, 'Low', 'Wine', 'White Labs', 'WLP749', 'Liquid', '', 'German red wine yeast, which results in spicy, fruit aromas. Perfect for Pinot Noir and Zinfandel. Slow to moderate fermenter which is cold tolerant.  Alcohol Tolerance: 16%|Pinot Noir and Zinfandel', '', '50', '90', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('143', 'Australian Ale', '70', '75', null, 'High', 'Ale', 'White Labs', 'WLP009', 'Liquid', '', 'White Labs entry for Australian Ales.  Produces a clean, malty finish with pleasant ester character.  Bready character.  Can ferment clean at high temperatures.|Australian Ales, English Ales', '', '65', '70', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('144', 'Avize Wine', '80', '86', null, 'Medium', 'Wine', 'White Labs', 'WLP718', 'Liquid', '', 'Champagne isolate used for complexity in whites. Contributes elegance, especially in barrel fermented Chardonnays.  Alcohol tolerance to 15%.|Chardonnay', '', '60', '90', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('145', 'Bastogne Belgian Ale', '74', '80', null, 'Medium', 'Ale', 'White Labs', 'WLP510', 'Liquid', '', 'High gravity Trappist ale yeast.  Creates a dry beer with a slightly acidic finish.  Cleaner finish and slightly less spicy than WLP500 or WLP530.  |High gravity beers, Belgian ales, Dubbels, Trippels.', '', '66', '72', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('146', 'Bavarian Weizen', '73', '77', null, 'Low', 'Wheat', 'White Labs', 'WLP351', 'Liquid', '', 'Former yeast lab W51 strain.  Produces a classic German style wheat beer with moderately high, spicy, phenolic overtones reminiscent of cloves.|Bavarian Weizen and wheat beers.', '', '66', '70', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('147', 'Bedford British Ale', '72', '80', null, 'High', 'Ale', 'White Labs', 'WLP006', 'Liquid', '', 'High attenuation. Ferments dry with high flocculation. Distinctive ester profile.  Good for most English ale styles.|English style ales - bitter, pale, porter and brown ale', '', '65', '70', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('148', 'Belgian Golden Ale', '73', '78', null, 'Low', 'Ale', 'White Labs', 'WLP570', 'Liquid', '', 'Combination of fruitiness and phenolic characters dominate the profile.  Some sulfur which will dissapate following fermentation.|Belgian Ales, Dubbel, Grand Cru, Belgian Holiday Ale', '', '68', '75', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('149', 'Belgian Lager', '72', '78', null, 'Medium', 'Lager', 'White Labs', 'WLP815', 'Liquid', '', 'Clean, crisp European lager yeast with low sulfur  production. The strain originates from a very old brewery in West Belgium.  Great for European style pilsners, dark  lagers, Vienna lager, and American style lagers.  |European style pilsners, dark  lagers, Vienna lager, and American style lagers', '', '50', '55', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('150', 'Belgian Saison I Ale', '65', '75', null, 'Medium', 'Ale', 'White Labs', 'WLP565', 'Liquid', '', 'Saison yeast from Wallonia.  Earthy, spicy and peppery notes.  Slightly sweet.|Saison Ale, Belgian Ale, Dubbel, Trippel', '', '68', '75', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('151', 'Belgian Saison II', '78', '85', null, 'Medium', 'Ale', 'White Labs', 'WLP566', 'Liquid', '', 'Saison strain with a more fruity ester profile than WLP565 (Belgian Saison I Yeast).  Moderately phenolic with a clove-like characteristic in finished beer flavor and aroma.  Ferments quickly.|Belgian or French Saison', '', '68', '78', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('152', 'Belgian Saison', '76', '80', null, 'Low', 'Ale', 'Wyeast', '3724', 'Liquid', '', 'Classic farmhouse ale yeast.  Spicy, complex aromatics including bubble gum.  Tart and dry on the palate with mild fruitiness.  Finishes crisp and mildly acidic.  Ferment at warm temperature.  May have vigorous fermentation start.|Belgian Saison beer', '', '70', '95', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('153', 'Belgian Sour Mix 1', '68', '72', null, 'Medium', 'Ale', 'White Labs', 'WLP655', 'Liquid', '', 'Note: Bacteria to use in secondary only.  A unique blend perfect for Belgian style beers. Includes Brettanomyces, Saccharomyces, and the bacterial strains Lactobacillus and Pediococcus.|Belgian sour beers (in secondary)', '', '65', '72', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('154', 'Belgian Strong Ale', '78', '85', null, 'Medium', 'Ale', 'White Labs', 'WLP545', 'Liquid', '', 'From the Ardennes region of Belgium, this classic strain produces moderate esters and spicy phenolic character.  Results in a dry but balanced finish.  Use for dark or strong abbey ales.|Belgian dark strongs, Abbey ales and Christmas beers', '', '65', '73', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('155', 'Belgian Style Ale Yeast Blend', '74', '80', null, 'Medium', 'Ale', 'White Labs', 'WLP575', 'Liquid', '', 'Blend of two trappist ale yeasts and one Belgian ale yeast.  Creates a versatile blend to be used for Trappist and other Belgian style ales.|Trappist and other Belgian ales.', '', '68', '75', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('156', 'Belgian Style Saison Ale Yeast Blend', '70', '80', null, 'Medium', 'Ale', 'White Labs', 'WLP568', 'Liquid', '', 'This blend melds Belgian style ale and Saison strains. The strains work in harmony to create complex, fruity aromas and flavors. The blend of yeast strains encourages complete fermentation in a timely manner. Phenolic, spicy, earthy, and clove like flavor|Belgian and French Saison', '', '70', '80', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('157', 'Belgian Wit II', '70', '75', null, 'Medium', 'Ale', 'White Labs', 'WLP410', 'Liquid', '', 'Less phenolic than WLP400 (Belgian Wit Ale) but more spicy.  Leaves a little more sweetness and flocculation is higher than WLP400.|Belgian Wit, Spiced Ale, Wheat Ales and Specialty Beers', '', '67', '74', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('158', 'Berliner Weisse Blend', '73', '80', null, 'Medium', 'Wheat', 'White Labs', 'WLP630', 'Liquid', '', 'A blend of a traditional German Weizen yeast and  Lactobacillus to create a subtle, tart, drinkable beer. Can  take several months to develop tart character.  Perfect for traditional Berliner Weisse.|Berliner Weisse', '', '68', '72', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('159', 'Blanche Ale', '71', '75', null, 'Medium', 'Wheat', 'Brewferm', 'Y015', 'Dry', '', 'Top fermenting strain for Belgian witbier and wheats.  Spicy and lightly phenolic.|Wheat beer, Wit', '', '64', '73', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('160', 'Brettanomyces Bruxellensis', '68', '72', null, 'Medium', 'Ale', 'White Labs', 'WLP650', 'Liquid', '', 'Medium intensity Brett character. Classic strain used in secondary fermentation for Belgian style beers and lambics. One Trappist brewery uses this strain in secondary fermentation.|Belgian sour ales and labics (in secondary)', '', '65', '72', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('161', 'Brettanomyces Bruxellensis', '80', '84', null, 'Medium', 'Ale', 'Wyeast', '5112', 'Liquid', '', 'This strain of wild yeast was isolated from brewery cultures in the Brussels region of Belgium. It produces the classic sweaty horse blanket character and may form a pellicle in bottles or casks. The strain is generally used in conjunction with S. cerevis|Gueuze, Lambics and Sour Browns ', '', '60', '75', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('162', 'Brettanomyces Claussenii', '68', '72', null, 'Medium', 'Ale', 'White Labs', 'WLP645', 'Liquid', '', 'Low intensity Brett character. Originally isolated from strong English stock beer, in the early 20th century. The Brett flavors produced are more subtle than WLP650 and WLP653. More aroma than flavor contribution. Fruity, pineapple like aroma.|Sour ales (in secondary)', '', '65', '72', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('163', 'Brettanomyces Lambicus', '68', '72', null, 'Medium', 'Ale', 'White Labs', 'WLP653', 'Liquid', '', 'Use in secondary.  High intensity Brett character. Defines the \"Brett character\": Horsey, smoky and spicy flavors. As the name suggests, this strain is found most often in Lambic style beers, which are spontaneously fermented beers.|Lambics and Flanders/Sour Brown ales', '', '65', '72', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('164', 'Brettanomyces Lambucus', '78', '82', null, 'Very High', 'Ale', 'Wyeast', '5526', 'Liquid', '', 'This is a wild yeast strain isolated from Belgian lambic beers. It produces a pie cherry-like flavor and sourness with a distinct Brett character. A pellicle may form in bottles or casks. This strain works best in conjunction with other yeast and lactic b|Lambic', '', '60', '75', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('165', 'British Cask Ale', '74', '77', null, 'Medium', 'Ale', 'Wyeast', '1026', 'Liquid', '', 'A great choice for any cask conditioned British Ale.  |Produces nice malt profile with a hint of fruit. Finishes dry and slightly tart. |British Ales', '', '63', '72', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('166', 'Budvar Lager', '71', '75', null, 'High', 'Lager', 'Wyeast', '2000', 'Liquid', '', 'Classic pilsner lager yeast.  Malty nose and subtle fruit.  Rich malt profile, but dry crisp finish.  Hop character accentuated by dry finish.|Bohemian Pilsner, Classic Pilsners, Dortmunder and Light Lagers', '', '46', '56', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('167', 'Cabernet Red Wine', '80', '86', null, 'Low', 'Wine', 'White Labs', 'WLP760', 'Liquid', '', 'High temperature tolerance. Moderate fermentation speed. Excellent for full-bodied red wines, ester production complements flavor. WLP760 is also suitable for Merlot, Chardonnay, Chianti, Chenin Blanc, and Sauvignon Blanc.|Alcohol Tolerance: 16%|Merlot, Chardonnay, Chianti, Chenin Blanc, and Sauvignon Blanc', '', '60', '90', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('168', 'California Ale V', '70', '75', null, 'High', 'Ale', 'White Labs', 'WLP051', 'Liquid', '', 'Similar to White Labs California Ale Yeast, but slightly lower attenuation leaves a fuller bodied beer.|American style Pales, Ambers, Browns, IPAs, American Strong Ale', '', '66', '70', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('169', 'Chardonnay White Wine', '80', '86', null, 'Low', 'Wine', 'White Labs', 'WLP730', 'Liquid', '', 'Dry wine yeast. Slight ester production, low sulfur dioxide production. Enhances varietal character. WLP730 is a good choice for all white and blush wines, including Chablis, Chenin Blanc, Semillon, and Sauvignon Blanc. Fermentation speed is moderate.  Al|Chardonnay Wine', '', '50', '90', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('171', 'Cream Ale Yeast Blend', '75', '80', null, 'Medium', 'Ale', 'White Labs', 'WLP080', 'Liquid', '', 'A blend of ale and lager yeast strains that work together to create a clean, light American lager style ale.  A pleasing estery aroma may be perceived.  Hop flavors and bitterness are slightly subdued.  Slight sulfer will be produced during fermentation f|Cream Ale, Hybrids', '', '65', '70', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('172', 'Cry Havoc', '66', '70', null, 'Medium', 'Lager', 'White Labs', 'WLP862', 'Liquid', '', 'Licensed by White Labs from Charlie Papazian, author of \"The Complete Joy of Home Brewing\".  This yeast was used to brew many of his original recipes.  Diverse strain can ferment at ale and lager temps.|All American Style Lagers -- both light and dark.', '', '68', '74', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('173', 'Czech Budejovice Lager', '75', '80', null, 'Medium', 'Lager', 'White Labs', 'WLP802', 'Liquid', '', 'Dry and crisp with low diacetyl production.  From Southern Czech Republic.|Bohemian Style Pilsner', '', '50', '55', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('174', 'Diamond Lager', '71', '75', null, 'Medium', 'Lager', 'Lallemand', '-', 'Dry', '', 'Diamond Lager yeast originated in Germany and is used in a number of commercial breweries to produce various lagers.|Lagers', '', '50', '59', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('176', 'Dry English Ale', '70', '80', null, 'High', 'Ale', 'White Labs', 'WLP007', 'Liquid', '', 'Clean, Highly flocculant, and highly attentive yeast.  Similar to White labs English Ale yeast, but more attentive.  Suitable for high gravity ales.|Pale Ales, Amber, ESB, Brown Ales, Strong Ales', '', '65', '70', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('177', 'Dusseldorf Alt', '65', '72', null, 'Medium', 'Ale', 'White Labs', 'WLP036', 'Liquid', '', 'Traditional Alt yeast from Dusseldorf, Germany.  Produces clean, slightly sweet alt beers.  Does not accentuate hop flavor like WLP029 does.|Alt biers, Dusseldorf Alts, German Ales', '', '65', '69', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('179', 'English Cider', '80', '86', null, 'Medium', 'Wine', 'White Labs', 'WLP775', 'Liquid', '', 'Classic Cider yeast.  Ferments dry, but retains apple flavor.  Some sulfer produced during fermentation will fade with age.|Cider, Wine and High Gravity Beer', '', '68', '75', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('180', 'Essex Ale', '71', '76', null, 'Medium', 'Ale', 'White Labs', 'WLP022', 'Liquid', '', 'Flavorful British yeast with a drier finish than many ale yeasts.  Bready and fruity in character.  Well suited for top cropping (collecting).  Does not flocculate as much as WLP005 or WLP002.|British milds, pale ales, bitters, stouts.', '', '66', '70', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('181', 'European Ale', '65', '70', null, 'Medium', 'Ale', 'White Labs', 'WLP011', 'Liquid', '', 'Malty, Northern European ale yeast.  Low ester production, low sulfer, gives a clean profile.  Low attenuation contributes to malty taste.|Alt, Kolsch, malty English Ales, Fruit beers', '', '65', '70', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('182', 'Flor Sherry', '80', '86', null, 'Medium', 'Wine', 'White Labs', 'WLP700', 'Liquid', '', 'This yeast develops a film (flor) on the surface of the wine. Creates   green almond, granny smith and nougat characteristics found in sherry. Can also be used for Port, Madeira and other sweet styles. For use in secondary fermentation. Slow fermentor. Al|Sherry wine yeast', '', '70', '76', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('183', 'French Red Wine', '80', '86', null, 'Low', 'Wine', 'White Labs', 'WLP750', 'Liquid', '', 'Classic Bordeaux yeast for red wine fermentations. Moderate fermentation characteristics. Tolerates lower fermentation temperatures. Rich, smooth flavor profile.  Alcohol Tolerance: 17%|Bordeaux', '', '60', '90', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('184', 'French Saison', '77', '83', null, 'Medium', 'Ale', 'Wyeast', '3711', 'Liquid', '', 'A very versatile strain that produces Saison or farmhouse style biers as well as other Belgian style beers that are highly aromatic (estery), peppery, spicy and citrusy. This strain enhances the use of spices and aroma hops, and is extremely attenuative b|French and Belgian Saison or Farmhouse ales', '', '65', '77', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('185', 'French White Wine', '80', '86', null, 'Low', 'Wine', 'White Labs', 'WLP735', 'Liquid', '', 'Classic yeast for white wine fermentation. Slow to moderate fermenter and foam producer. Gives an enhanced creamy texture.  Alcohol Tolerance: 16%|French white wines', '', '60', '90', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('186', 'German Ale II', '73', '80', null, 'Medium', 'Ale', 'White Labs', 'WLP003', 'Liquid', '', 'Strong sulfer component will reduce with aging.  Clean flavor, but with more ester production than regular German Ale Yeast.|Kolsch, Alt and German Pale Ales', '', '65', '70', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('187', 'German Bock Lager', '70', '76', null, 'Medium', 'Lager', 'White Labs', 'WLP833', 'Liquid', '', 'Produces beer that has balanced malt and hop character.  From Southern Bavaria.|Bocks, Doppelbocks, Oktoberfest, Vienna, Helles, some American Pilsners', '', '48', '55', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('188', 'Klassic Ale', '66', '74', null, 'Medium', 'Ale', 'White Labs', 'WLP033', 'Liquid', '', 'Traditional English Ale style yeast.  Produces ester character, and allows hop flavor through.  Leaves a slightly sweet malt character in ales.|Bitters, milds, porters stouts and scottish ale styles.', '', '66', '70', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('189', 'Lactobacillus Bacteria', '68', '72', null, 'Medium', 'Ale', 'White Labs', 'WLP677', 'Liquid', '', 'Bacteria for use in secondary.  This lactic acid bacteria produces moderate levels of acidity and sour flavors found in lambics, Berliner Weiss, sour brown ale and gueze.|Lambic, Berliner Weiss, Sour Brown and Gueze (secondary)', '', '65', '72', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('190', 'Lactobacillus', '78', '82', null, 'Medium', 'Ale', 'Wyeast', '5335', 'Liquid', '', 'Lactic acid bacteria isolated from a Belgian brewery. This culture produces moderate levels of acidity and is commonly found in many types of beers including gueuze, lambics, sour brown ales and Berliner Weisse. It is always used in conjunction with S.cer|Belgian sout beers (secondary)', '', '60', '95', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('191', 'Lager', '76', '80', null, 'Medium', 'Lager', 'Brewferm', 'Y016', 'Dry', '', 'A dry lager yeast with high attenuation.  Ferments clean and malty.|Lagers', '', '50', '59', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('192', 'Lalvin - K1V-1116', '73', '77', null, 'Medium', 'Wine', 'Lallemand', 'K1V-1116', 'Dry', '', 'Used for white grape varieties.  Rapid starter with constant and complete fermentation. Capable of surviving difficult conditions such as low nutrient or high SO2 levels.  Has low volatile action.|Souvingnon Blanc, Chenin Blanc and Seyval.  ', '', '59', '86', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('193', 'Lalvin 71B-1122', '73', '77', null, 'High', 'Wine', 'Lallemand', '71B-1122', 'Dry', '', 'Rapid starter with constant and complete fermentation. Ability to metabolize high amounts (20-40%) of malic acid. Partial metabolism of malic acid helps soften the wine.  May produce significant esters, making it a good choice for concentrates. |Young wines such as nouveau, blush and sugar white.', '', '59', '86', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('194', 'Lalvin D-47', '73', '77', null, 'High', 'Wine', 'Lallemand', 'D-47', 'Dry', '', 'Recommended for white variety wines such as Chardonnay and Rose as well as Mead.  Low foaming, quick fermenting, forming a compact lees at the end of fermentation.  Use yeast nutrients if making mead.  Saccharomyces Cerevisiae.|White wines such as Chardonnay and Rose.  Also good for mead.', '', '50', '86', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('195', 'Lalvin EC-1118', '73', '77', null, 'High', 'Wine', 'Lallemand', 'EC-1118', 'Dry', '', 'Low production of foam, volatile acid and H2S.  Ferments over a wide temperature range. High alcohol tolerance, compact lees and good flocculation.  Relatively neutral flavor and aroma.|All types of wine and also cider.', '', '45', '95', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('196', 'Lalvin RC 212 (Bourgovin)', '73', '77', null, 'Medium', 'Wine', 'Lallemand', 'RC 212', 'Dry', '', 'RC212 recommended for red variety wines and high gravity beers.  Alcohol tolerance in the 12-14% range.  Low foaming and moderate speed fermenting.  Saccharomyces Cerevisiae.|Red wines.', '', '59', '86', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('197', 'London Ale', '67', '75', null, 'Medium', 'Ale', 'White Labs', 'WLP013', 'Liquid', '', 'Dry, malty ale yeast.  Produces a complex, oak flavored ester character.  Hop bitterness comes through well.|Classic British Pale Ales, Bitters and Stouts', '', '66', '71', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('199', 'Malolactic Bacteria', '68', '72', null, 'Medium', 'Ale', 'White Labs', 'WLP675', 'Liquid', '', 'Bacteria for use in secondary.  Malolactic fermentation is the conversion of malic acid to lactic acid by bacteria from the lactic acid bacteria family.  Lactic acid is less acidic than malic acid, which in turn decreases acidity and helps to soften and/o|Primarily wine', '', '65', '72', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('200', 'Manchester Ale', '70', '74', null, 'Medium', 'Ale', 'White Labs', 'WLP038', 'Liquid', '', 'Top fermenting strain that is good for top-cropping.  Moderately flocculent with a clean, dry finish.  Low ester profile for producing a balanced English ale.|English style ales', '', '65', '70', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('202', 'Merlot Red Wine', '80', '86', null, 'Low', 'Wine', 'White Labs', 'WLP740', 'Liquid', '', 'Neutral, low fusel alcohol production. Will ferment to dryness, alcohol tolerance to 18%. Vigorous fermenter. WLP740 is well suited for Merlot, Shiraz, Pinot Noir, Chardonnay, Cabernet, Sauvignon Blanc, and Semillon.  Alcohol Tolerance: 18%|Merlot, Shiraz, Pinot Noir, Chardonnay, Cabernet, Sauvignon Blanc, and Semillon', '', '60', '90', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('203', 'Mexican Lager', '70', '78', null, 'Medium', 'Lager', 'White Labs', 'WLP940', 'Liquid', '', 'From Mexico City - produces a clean lager beer with a crisp finish.  Good for mexican style beers.|Mexican style light and dark lagers.', '', '50', '55', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('205', 'Munuch', '68', '72', null, 'Medium', 'Wheat', 'Lallemand', '- ', 'Dry', '', 'Danstar Munich Wheat Beer yeast originated in Bavaria, Germany, the home of many of the worlds great wheat beer breweries.|Wheat beers', '', '65', '71', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('206', 'Northeastern Micro Ale', '73', '77', null, 'Medium', 'Ale', 'Brewtek', 'CL-0060', 'Liquid', '', 'Malty, bready, yet clean malt character.  Leaves hops flavor and aroma intact.  Versitile yeast for many American styles.|American Ales, Reds, Ambers', '', '56', '72', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('207', 'Nottingham Ale', '73', '82', null, 'Medium', 'Ale', 'White Labs', 'WLP039', 'Liquid', '', 'British style of ale yeast with a very dry finish and high attenuation.  Medium to low fruit and fusel alcohol production.  Good top fermenting yeast for cropping. |British ales, pale ales, ambers, porters and stouts.', '', '66', '70', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('208', 'Nottingham', '73', '77', null, 'High', 'Ale', 'Lallemand', '-', 'Dry', '', 'High flocculation - settles quickly.  Very good reputation as a fast starter and quick fermenter.  Clean and only slightly fruity.  Some nutty flavor in bottled version.  Relatively high attenuation.|Ales', '', '57', '70', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('209', 'Octoberfest Lager Blend', '73', '77', null, 'Low', 'Lager', 'Wyeast', '2633', 'Liquid', '', 'This blend of lager strains is designed to produce a rich, malty, complex and full bodied Octoberfest style beer. It attenuates well while leaving plenty of malt character and mouthfeel. This strain is low in sulfur production. |Octoberfest, Marzen, Bavarian lagers', '', '48', '58', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('210', 'Old Bavarian Lager', '66', '73', null, 'Medium', 'Lager', 'White Labs', 'WLP920', 'Liquid', '', 'Southern Germany/Bavarian lager yeast.  Finishes malty with a slight ester profile.|Oktoberfest, Marzen, Bock and Dark Lagers.', '', '50', '55', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('211', 'Pacific Ale', '65', '70', null, 'High', 'Ale', 'White Labs', 'WLP041', 'Liquid', '', 'Popular yeast from the Pacific Northwest.  Leaves a clear and malty profile.  More fruity than WLP002.  Suitable for many English and American styles.|English & American ales including milds, bitters, IPA, porters and English stouts.', '', '65', '68', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('213', 'Pediococcus', '78', '82', null, 'Medium', 'Ale', 'Wyeast', '5733', 'Liquid', '', 'Lactic acid bacteria used in the production of Belgian style beers where additional acidity is desirable. Often found in gueuze and other Belgian style beer. Acid production will increase with storage time. It may also cause ropiness and produce low level|Belgian sour ales', '', '65', '95', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('214', 'Premium Bitter Ale', '70', '75', null, 'Medium', 'Ale', 'White Labs', 'WLP026', 'Liquid', '', 'From Staffordshire England.  Mild, but complex estery flavor.  High attenuation - ferments strong and dry.  Suitable for high gravity beers.|All English ales including bitters, milds, ESB, Porter, Stout and Barley Wine', '', '67', '70', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('216', 'Roselare Belgian Blend', '78', '82', null, 'Medium', 'Ale', 'Wyeast', '3763', 'Liquid', '', 'Culture of Saccharomyces, Brettonomyces and Lactic Acid Bacteria.  Complex aromas and flavors.  May be used for primary fermentation.  Primarily for sour brown and red Belgian styles.|Belgian sour brown and red beers.', '', '55', '80', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('217', 'Safale American ', '73', '80', null, 'Medium', 'Ale', 'Fermentis', 'US-05', 'Dry', '', 'American ale yeast that produces well balanced beers with low diacetyl and a very clean, crisp end palate.|American ale, other clean finish ales', '', '59', '75', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('218', 'SafAle English Ale', '71', '75', null, 'Medium', 'Ale', 'Fermentis', 'S-04', 'Dry', '', 'Fast starting, fast fermenting yeast.  Quick attenuation helps to produce a clean, crisp, clear ale.  Can be used in a wide range of ales.|Great general purpose ale yeast.', '', '59', '75', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('219', 'SafAle German Ale', '71', '75', null, 'Medium', 'Ale', 'Fermentis', 'K-97', 'Dry', '', 'Low sedimentation yeast, sometimes used in open fermentation. Good for wheat beers, weizens and light ales.|High attenuation ales, wheat beers and weizens.', '', '59', '75', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('220', 'SafBrew Ale', '71', '75', null, 'Medium', 'Ale', 'Fermentis', 'S-33', 'Dry', '', 'General purpose ale yeast, widely used.  Very consistent, clean finish. High attenuation and good flavor profile.|Most ales.', '', '65', '72', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('221', 'SafBrew Specialty Ale', '71', '75', null, 'Medium', 'Ale', 'Fermentis', 'T-58', 'Dry', '', 'Estery, somewhat spicy ale yeast. Solid yeast formation at end of fermentation.  Widely used for bottle and cask conditioning.|Complex ales.', '', '60', '72', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('222', 'Safbrew Wheat', '66', '70', null, 'Medium', 'Wheat', 'Fermentis', 'WB-06', 'Dry', '', 'A specialty yeast for wheat beer fermentation.  The yeast produces a subtle estery and phenlol flavor typical of wheat beers.|Wheat beers', '', '59', '75', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('223', 'SafLager German Lager', '70', '74', null, 'High', 'Lager', 'Fermentis', 'S-189', 'Dry', '', 'Popular lager yeast strain.  Produces wide range of continental lagers and pilsners.  Clean finish.|Wide range of lagers and pilsners.', '', '48', '56', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('224', 'Saflager Lager', '73', '77', null, 'High', 'Lager', 'Fermentis', 'W-34/70', 'Dry', '', 'A famous yeast strain from Weihenstephan Germany used worldwide in brewing.  Their most popular strain for lagers.|European lagers', '', '48', '59', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('225', 'SafLager West European Lager', '71', '75', null, 'High', 'Lager', 'Fermentis', 'S-23', 'Dry', '', 'German lager yeast strain.  Performs well at low temperature. High flocculation and attenuation for a clean German finish.|German style Lagers and Pilsners.', '', '46', '50', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('226', 'Sake', '80', '86', null, 'Medium', 'Wine', 'White Labs', 'WLP705', 'Liquid', '', 'For use in rice based fermentations. For sake, use this yeast in conjunction with Koji (to produce fermentable sugar). WLP705 produces full body sake character, and subtle fragrance.  Alcohol tolerance to 16%.|Sake wine yeast', '', '70', '76', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('227', 'Southern German Lager', '68', '76', null, 'High', 'Lager', 'White Labs', 'WLP838', 'Liquid', '', 'Malty finish and balanced aroma.  Strong fermenter, slight sulfur and low diacetyl.|German Pilsner, Helles, Oktoberfest, Marzen, Bocks', '', '50', '55', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('228', 'Southwold Ale', '68', '75', null, 'Medium', 'Ale', 'White Labs', 'WLP025', 'Liquid', '', 'From Suffolk county.  Products complex fruity and citrus flavors.  Slight sulfer production, but this will fade with ageing.|British bitters and pale ales.', '', '66', '69', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('230', 'Steinberg-Geisenheim Wine', '80', '86', null, 'Low', 'Wine', 'White Labs', 'WLP727', 'Liquid', '', 'German origin wine yeast.  High fruit/ester production.  Moderate fermentation characteristics and cold tolerant.|Riesling wines.', '', '50', '90', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('231', 'Super High Gravity Ale', '80', '84', null, 'Low', 'Ale', 'White Labs', 'WLP099', 'Liquid', '', 'Ferments up to 25% alcohol content.  Flavor may vary greatly depending on beer alcohol.  English like esters at low gravity, but will become more wine-like as alcohol exceeds 16% ABV.  Refer to White Labs web page for tips on fermenting high gravity ales.|Very high gravity beers and barley wine up to 25% alcohol.', '', '69', '74', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('232', 'Suremain Burgundy Wine', '80', '86', null, 'Low', 'Wine', 'White Labs', 'WLP770', 'Liquid', '', 'Emphasizes fruit aromas in barrel fermentations. High nutrient requirement to avoid volatile acidity production. |Alcohol Tolerance: 16%|Burgundy', '', '60', '90', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('233', 'Urquell Lager', '72', '76', null, 'Medium', 'Lager', 'Wyeast', '2001', 'Liquid', '', 'Pilsner Urquell yeast with mild fruit/floral aroma.  Very dry and clean on palate with full mouth feel.  Subtle malt character.  Clean and neutral finish.|Bohemian Pilsner', '', '48', '58', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('234', 'Windsor', '73', '77', null, 'Very High', 'Ale', 'Lallemand', '-', 'Dry', '', 'Clean, well balanced finish.  Yeast produces an estery ale with a slighly fresh yeast flavor.  Not as quick as the Nottingham.  Some bannana aroma.|Ales', '', '62', '72', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('235', 'Yorkshire Square Ale', '68', '72', null, 'High', 'Ale', 'White Labs', 'WLP037', 'Liquid', '', 'This yeast produces a malty but well balanced profile.  Expect toasty flavors with malt driven esters.  Highly flocculent and a good choice for many English ales.|English pale ales, English brown ales and Mild ales', '', '65', '69', 'F', '2012-02-21 22:06:17');
INSERT INTO `yeast` VALUES ('236', 'Zurich Lager', '70', '80', null, 'Medium', 'Lager', 'White Labs', 'WLP885', 'Liquid', '', 'Swiss style lager yeast.  Sulfer and diacytl production is minimal.  May be used for high gravity lagers with proper care.|Swiss style lager, and high gravity lagers (over 11% ABV)', '', '50', '55', 'F', '2012-02-21 22:06:17');
